          Data: `[DongBaeJul Alert] ${subject}`,
          Charset: 'UTF-8'
        },
        Body: {
          Html: {
            Data: htmlBody,
            Charset: 'UTF-8'
          },
          Text: {
            Data: textBody,
            Charset: 'UTF-8'
          }
        }
      }
    };

    try {
      await ses.sendEmail(params).promise();
      logger.info('Alert email sent', { subject, recipients: this.adminEmails });
    } catch (error) {
      logger.error('Failed to send alert email', {
        error: error.message,
        subject
      });
