      // ì¬ì‹œë„ íì— ì¶”ê°€ (ì§€ìˆ˜ ë°±ì˜¤í”„)
      const retryDelay = Math.pow(2, item.retryCount) * 1000;

      setTimeout(() => {
        this.syncQueue.unshift(item);
