      reason: 'Participant lists merged'
    };
  }

  // ì±„íŒ… ë©”ì‹œì§€ ì¶©ëŒ í•´ê²°
  async resolveChatConflict(localMessages, remoteMessages) {
    // ë©”ì‹œì§€ ID ê¸°ë°˜ ë³‘í•©
    const messageMap = new Map();

    // ì›ê²© ë©”ì‹œì§€ ë¨¼ì € ì¶”ê°€
    remoteMessages.forEach(msg => {
