    const mergedMessages = Array.from(messageMap.values())
      .sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

    return {
      data: mergedMessages,
      resolution: 'merged',
      reason: 'Messages merged with offline message priority'
    };
  }
