      if (stored) {
        cacheEntry = JSON.parse(stored);

        if (Date.now() < cacheEntry.expiresAt) {
          // ë©”ëª¨ë¦¬ ìºì‹œì— ë‹¤ì‹œ ë¡œë“œ
          this.memoryCache.set(key, cacheEntry);
          logger.debug('Data retrieved from persistent cache', { key });
