```javascript
const AnimationComponents = {
  // ìŠ¤ì¼€ì¼ í”„ë ˆìŠ¤ ì• ë‹ˆë©”ì´ì…˜
  ScalePressable: ({ children, onPress }) => {
    const scaleAnim = useRef(new Animated.Value(1)).current;

    const handlePressIn = () => {
      Animated.timing(scaleAnim, {
        toValue: 0.95,
        duration: 100,
        useNativeDriver: true
      }).start();
    };

    const handlePressOut = () => {
      Animated.timing(scaleAnim, {
        toValue: 1,
        duration: 100,
        useNativeDriver: true
      }).start();
    };

    return (
      <Animated.View style={{ transform: [{ scale: scaleAnim }] }}>
        <TouchableOpacity
          onPressIn={handlePressIn}
          onPressOut={handlePressOut}
          onPress={onPress}
          activeOpacity={1}
        >
          {children}
        </TouchableOpacity>
      </Animated.View>
    );
  },

  // ì¹´ìš´íŠ¸ì—… ì• ë‹ˆë©”ì´ì…˜
  CountUpNumber: ({ from, to, duration = 500 }) => {
    const animatedValue = useRef(new Animated.Value(from)).current;
    const [displayValue, setDisplayValue] = useState(from);

    useEffect(() => {
      const listener = animatedValue.addListener(({ value }) => {
        setDisplayValue(Math.round(value));
      });

      Animated.timing(animatedValue, {
        toValue: to,
        duration,
        easing: Easing.out(Easing.cubic),
        useNativeDriver: false
      }).start();

      return () => animatedValue.removeListener(listener);
    }, [to]);

    return <Text style={styles.countNumber}>{displayValue}</Text>;
  },

  // ì„±ì·¨ ì• ë‹ˆë©”ì´ì…˜
  AchievementBurst: ({ visible, children }) => {
    const scaleAnim = useRef(new Animated.Value(0)).current;
    const rotateAnim = useRef(new Animated.Value(0)).current;

    useEffect(() => {
      if (visible) {
        Animated.sequence([
          Animated.timing(scaleAnim, {
            toValue: 1.2,
            duration: 200,
            useNativeDriver: true
          }),
          Animated.timing(scaleAnim, {
            toValue: 1,
            duration: 300,
            useNativeDriver: true
          })
        ]).start();

        Animated.sequence([
          Animated.timing(rotateAnim, {
            toValue: 1,
            duration: 100,
            useNativeDriver: true
          }),
          Animated.timing(rotateAnim, {
            toValue: -1,
            duration: 100,
            useNativeDriver: true
          }),
          Animated.timing(rotateAnim, {
            toValue: 0,
            duration: 100,
            useNativeDriver: true
          })
        ]).start();
      }
    }, [visible]);

    const rotate = rotateAnim.interpolate({
      inputRange: [-1, 1],
      outputRange: ['-10deg', '10deg']
    });

    return (
      <Animated.View 
        style={{
          transform: [
            { scale: scaleAnim },
            { rotate }
          ]
        }}
      >
        {children}
      </Animated.View>
    );
  }
};
