      const listener = animatedValue.addListener(({ value }) => {
        setDisplayValue(Math.round(value));
      });

      Animated.timing(animatedValue, {
        toValue: to,
        duration,
        easing: Easing.out(Easing.cubic),
        useNativeDriver: false
