      }
    }, [visible]);

    const rotate = rotateAnim.interpolate({
      inputRange: [-1, 1],
      outputRange: ['-10deg', '10deg']
    });

    return (
      <Animated.View 
        style={{
          transform: [
            { scale: scaleAnim },
            { rotate }
          ]
        }}
      >
        {children}
      </Animated.View>
    );
  }
};
