    setTimeout(() => {
      setRefreshing(false);
    }, 1000);
  };

  const renderMyTurnCard = () => {
    if (!currentGame?.nextOpponent) return null;

    return (
      <StatusCard
        title="ğŸ¯ ì² ìˆ˜ë‹˜ ì°¨ë¡€!"
