      {showUndo && (
        <View style={styles.undoContainer}>
          <LargeTouchButton
            title="ì‹¤ìˆ˜í–ˆë‚˜ìš”? ë˜ëŒë¦¬ê¸°"
            variant="outline"
            onPress={handleUndo}
            style={styles.undoButton}
          />
          <Text style={styles.undoTimer}>5ì´ˆ í›„ ìë™ìœ¼ë¡œ ì‚¬ë¼ì§‘ë‹ˆë‹¤</Text>
        </View>
      )}

