import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { colors } from '../utils/theme';

// í™”ë©´ë“¤
import HomeScreen from '../screens/HomeScreen';
import GameBoardScreen from '../screens/GameBoardScreen';
import CheckInScreen from '../screens/CheckInScreen';
import ScoreInputScreen from '../screens/ScoreInputScreen';

const Tab = createBottomTabNavigator();

export default function AppNavigator() {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          if (route.name === 'Home') {
            iconName = focused ? 'home' : 'home-outline';
          } else if (route.name === 'CheckIn') {
            iconName = focused ? 'qrcode-scan' : 'qrcode';
          } else if (route.name === 'GameBoard') {
            iconName = focused ? 'scoreboard' : 'scoreboard-outline';
          } else if (route.name === 'ScoreInput') {
            iconName = focused ? 'plus-circle' : 'plus-circle-outline';
          }

          return <MaterialCommunityIcons name={iconName} size={size} color={color} />;
        },
        tabBarActiveTintColor: colors.primary,
        tabBarInactiveTintColor: colors.textSecondary,
        tabBarStyle: {
          height: 60,
          paddingBottom: 8,
          paddingTop: 8,
        },
        tabBarLabelStyle: {
          fontSize: 12,
          fontWeight: '600',
        },
        headerStyle: {
          backgroundColor: colors.primary,
        },
        headerTintColor: '#FFFFFF',
        headerTitleStyle: {
          fontWeight: 'bold',
          fontSize: 18,
        },
      })}
    >
      <Tab.Screen 
        name="Home" 
        component={HomeScreen} 
        options={{ 
          title: 'í™ˆ',
          headerTitle: 'ğŸ¸ ë™ë°°ì¦'
        }} 
      />
      <Tab.Screen 
        name="CheckIn" 
        component={CheckInScreen} 
        options={{ 
          title: 'ì²´í¬ì¸',
          headerTitle: 'ì²´ìœ¡ê´€ ë„ì°©'
        }} 
      />
      <Tab.Screen 
        name="GameBoard" 
        component={GameBoardScreen} 
        options={{ 
          title: 'ê²Œì„í˜„í™©',
          headerTitle: 'ì‹¤ì‹œê°„ ê²Œì„ í˜„í™©'
        }} 
      />
      <Tab.Screen 
        name="ScoreInput" 
        component={ScoreInputScreen} 
        options={{ 
          title: 'ì ìˆ˜ì…ë ¥',
          headerTitle: 'ê²Œì„ ê²°ê³¼ ì…ë ¥'
        }} 
      />
    </Tab.Navigator>
  );
}

