    }
  },

  name: {
    required: '이름을 입력해주세요.',
    minLength: {
      value: 2,
      message: '이름은 최소 2자 이상이어야 합니다.'
    },
    maxLength: {
      value: 50,
      message: '이름은 최대 50자까지 입력 가능합니다.'
    }
  },

  phone: {
    required: '전화번호를 입력해주세요.',
    pattern: {
      value: /^01([0|1|6|7|8|9])-?([0-9]{3,4})-?([0-9]{4})$/,
      message: '유효한 전화번호를 입력해주세요.'
    }
  },

  clubName: {
    required: '클럽명을 입력해주세요.',
    minLength: {
      value: 2,
      message: '클럽명은 최소 2자 이상이어야 합니다.'
    },
    maxLength: {
      value: 100,
      message: '클럽명은 최대 100자까지 입력 가능합니다.'
    }
  },

  gameTitle: {
    required: '게임명을 입력해주세요.',
    minLength: {
      value: 2,
      message: '게임명은 최소 2자 이상이어야 합니다.'
    },
    maxLength: {
      value: 200,
      message: '게임명은 최대 200자까지 입력 가능합니다.'
    }
  },

  location: {
    required: '장소를 입력해주세요.',
    minLength: {
      value: 2,
      message: '장소는 최소 2자 이상이어야 합니다.'
    }
  },

  description: {
    maxLength: {
      value: 1000,
      message: '설명은 최대 1000자까지 입력 가능합니다.'
    }
  }
};

export const validateForm = (data, rules) => {
  const errors = {};

  Object.keys(rules).forEach(field => {
    const value = data[field];
    const rule = rules[field];

    if (rule.required && (!value || value.trim() === '')) {
      errors[field] = { type: 'required', message: rule.required };
      return;
    }

    if (value && rule.minLength && value.length < rule.minLength.value) {
      errors[field] = { type: 'minLength', message: rule.minLength.message };
      return;
    }

    if (value && rule.maxLength && value.length > rule.maxLength.value) {
      errors[field] = { type: 'maxLength', message: rule.maxLength.message };
      return;
    }

    if (value && rule.pattern && !rule.pattern.value.test(value)) {
      errors[field] = { type: 'pattern', message: rule.pattern.message };
      return;
    }
  });

  return errors;
};

export const isValidEmail = (email) => {
  return ValidationRules.email.pattern.value.test(email);
};

export const isValidPhone = (phone) => {
  return ValidationRules.phone.pattern.value.test(phone);
};

export const formatPhone = (phone) => {
  const cleaned = phone.replace(/\D/g, '');
  const match = cleaned.match(/^(\d{3})(\d{3,4})(\d{4})$/);
  if (match) {
    return `${match[1]}-${match[2]}-${match[3]}`;
  }
  return phone;
};

export const sanitizeInput = (input, maxLength = 1000) => {
  if (!input) return '';
  return input.trim().substring(0, maxLength);
};

export default {
  ValidationRules,
  validateForm,
  isValidEmail,
  isValidPhone,
  formatPhone,
  sanitizeInput
};

