const delay = (ms = 1000) => new Promise(resolve => setTimeout(resolve, ms));

export const bandAPI = {
  // Authentication
  async login(credentials) {
    await delay(800);
    if (credentials.email && credentials.password) {
      return {
        success: true,
        data: {
          user: mockData.user,
          token: 'mock_jwt_token_here'
        }
