  async getProfile() {
    await delay(500);
    return {
      success: true,
      data: mockData.user
    };
  },

  async updateProfile(profileData) {
    await delay(600);
    mockData.user = { ...mockData.user, ...profileData };
    return {
      success: true,
      data: mockData.user
    };
  },

  // Clubs
  async getClubs() {
    await delay(800);
    return {
      success: true,
      data: mockData.clubs.filter(club => 
        mockData.user.clubs.includes(club.id)
      )
    };
  },

  async searchClubs(query, filters = {}) {
    await delay(1000);
    let results = mockData.clubs;

    if (query) {
      results = results.filter(club =>
        club.name.toLowerCase().includes(query.toLowerCase()) ||
        club.description.toLowerCase().includes(query.toLowerCase())
      );
    }

    if (filters.location) {
      results = results.filter(club =>
        club.location.includes(filters.location)
      );
    }

    return {
      success: true,
      data: results
    };
  },

  async joinClub(clubId) {
    await delay(700);
    if (!mockData.user.clubs.includes(clubId)) {
      mockData.user.clubs.push(clubId);
    }
    const club = mockData.clubs.find(c => c.id === clubId);
    if (club) {
      club.memberCount += 1;
      club.isJoined = true;
    }
    return { success: true };
  },

  async leaveClub(clubId) {
    await delay(500);
    mockData.user.clubs = mockData.user.clubs.filter(id => id !== clubId);
    const club = mockData.clubs.find(c => c.id === clubId);
    if (club) {
      club.memberCount -= 1;
      club.isJoined = false;
    }
    return { success: true };
  },

