    };
    mockData.games.push(newGame);
    return {
      success: true,
      data: newGame
    };
  },

  async joinGame(gameId) {
    await delay(500);
    const game = mockData.games.find(g => g.id === gameId);
    if (game && game.currentPlayers < game.maxPlayers) {
      game.currentPlayers += 1;
      return { success: true };
    }
    throw new Error('Game is full or not found');
  },

  async leaveGame(gameId) {
