/**
 * ì „ì—­ ì—ëŸ¬ í•¸ë“¤ë§ ìœ í‹¸ë¦¬í‹°
 */

import { Alert } from 'react-native';
import { logger } from './logger';
import { showMessage } from 'react-native-flash-message';

export class AppError extends Error {
  constructor(message, code, statusCode = 500) {
    super(message);
    this.name = 'AppError';
    this.code = code;
    this.statusCode = statusCode;
  }
}

export const ErrorCodes = {
  NETWORK_ERROR: 'NETWORK_ERROR',
  AUTH_ERROR: 'AUTH_ERROR',
  VALIDATION_ERROR: 'VALIDATION_ERROR',
  SERVER_ERROR: 'SERVER_ERROR',
  PERMISSION_DENIED: 'PERMISSION_DENIED',
  NOT_FOUND: 'NOT_FOUND',
  UNKNOWN_ERROR: 'UNKNOWN_ERROR'
};

export const ErrorMessages = {
  [ErrorCodes.NETWORK_ERROR]: 'ì¸í„°ë„· ì—°ê²°ì„ í™•ì¸í•´ì£¼ì„¸ìš”.',
  [ErrorCodes.AUTH_ERROR]: 'ë¡œê·¸ì¸ì´ í•„ìš”í•©ë‹ˆë‹¤.',
  [ErrorCodes.VALIDATION_ERROR]: 'ì…ë ¥ ì •ë³´ë¥¼ ë‹¤ì‹œ í™•ì¸í•´ì£¼ì„¸ìš”.',
  [ErrorCodes.SERVER_ERROR]: 'ì„œë²„ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤. ì ì‹œ í›„ ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.',
  [ErrorCodes.PERMISSION_DENIED]: 'ê¶Œí•œì´ ì—†ìŠµë‹ˆë‹¤.',
  [ErrorCodes.NOT_FOUND]: 'ìš”ì²­í•œ ì •ë³´ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.',
  [ErrorCodes.UNKNOWN_ERROR]: 'ì•Œ ìˆ˜ ì—†ëŠ” ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.'
};

export const handleError = (error, context = '') => {
  let errorCode = ErrorCodes.UNKNOWN_ERROR;
  let message = ErrorMessages[ErrorCodes.UNKNOWN_ERROR];

  if (error instanceof AppError) {
    errorCode = error.code;
    message = error.message;
  } else if (error.response) {
    // API ì‘ë‹µ ì—ëŸ¬
    const { status, data } = error.response;
    if (status >= 400 && status < 500) {
      errorCode = ErrorCodes.VALIDATION_ERROR;
      message = data?.message || ErrorMessages[ErrorCodes.VALIDATION_ERROR];
    } else if (status >= 500) {
      errorCode = ErrorCodes.SERVER_ERROR;
      message = ErrorMessages[ErrorCodes.SERVER_ERROR];
    }
  } else if (error.request) {
    // ë„¤íŠ¸ì›Œí¬ ì—ëŸ¬
    errorCode = ErrorCodes.NETWORK_ERROR;
    message = ErrorMessages[ErrorCodes.NETWORK_ERROR];
  } else if (error.message) {
    message = error.message;
  }

  // ë¡œê¹…
  logger.error(`${context ? `[${context}] ` : ''}${errorCode}: ${message}`, error);

  return { errorCode, message };
};

export const showErrorMessage = (error, context = '') => {
  const { message } = handleError(error, context);

  showMessage({
    message: 'ì˜¤ë¥˜ ë°œìƒ',
    description: message,
    type: 'danger',
    duration: 4000,
  });
};

export const showErrorAlert = (error, context = '', callback) => {
  const { message } = handleError(error, context);

  Alert.alert(
    'ì˜¤ë¥˜ ë°œìƒ',
    message,
    [
      {
        text: 'í™•ì¸',
        onPress: callback
      }
    ]
  );
};

export const withErrorHandler = (asyncFn, context = '') => {
  return async (...args) => {
    try {
      return await asyncFn(...args);
    } catch (error) {
      showErrorMessage(error, context);
      throw error;
    }
  };
};

export const safeAsyncCall = async (asyncFn, fallbackValue = null, context = '') => {
  try {
    return await asyncFn();
  } catch (error) {
    logger.error(`${context ? `[${context}] ` : ''}Safe async call failed:`, error);
    return fallbackValue;
  }
};

export default {
  AppError,
  ErrorCodes,
  ErrorMessages,
  handleError,
  showErrorMessage,
  showErrorAlert,
  withErrorHandler,
  safeAsyncCall
};

