  Surface,
  IconButton,
  ActivityIndicator,
  Divider
} from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useForm, Controller } from 'react-hook-form';
import * as ImagePicker from 'expo-image-picker';
import { useAuth } from '../../context/AuthContext';
import { userAPI } from '../../services/api';
import { theme } from '../../utils/theme';

const ProfileEditScreen = ({ navigation }) => {
  const { user, updateUser } = useAuth();
  const [isLoading, setIsLoading] = useState(false);
  const [isImageUploading, setIsImageUploading] = useState(false);
  const [profileImage, setProfileImage] = useState(user?.profileImage || null);

  const levelOptions = [
    { value: 'beginner', label: 'ì´ˆê¸‰' },
    { value: 'intermediate', label: 'ì¤‘ê¸‰' },
    { value: 'advanced', label: 'ê³ ê¸‰' },
    { value: 'expert', label: 'ì „ë¬¸ê°€' },
  ];

  const {
    control,
    handleSubmit,
    setValue,
    formState: { errors, isDirty },
  } = useForm({
    defaultValues: {
      name: user?.name || '',
      email: user?.email || '',
      phone: user?.phone || '',
      level: user?.level || 'beginner',
      preferredLocation: user?.preferredLocation || '',
      bio: user?.bio || '',
    },
  });

  useEffect(() => {
    // ê¶Œí•œ ìš”ì²­
    (async () => {
      const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('ê¶Œí•œ í•„ìš”', 'í”„ë¡œí•„ ì´ë¯¸ì§€ ë³€ê²½ì„ ìœ„í•´ ê°¤ëŸ¬ë¦¬ ì ‘ê·¼ ê¶Œí•œì´ í•„ìš”í•©ë‹ˆë‹¤.');
      }
    })();
  }, []);

  const onSubmit = async (data) => {
    try {
      setIsLoading(true);

      const updateData = {
        name: data.name,
        phone: data.phone,
        level: data.level,
        preferredLocation: data.preferredLocation,
        bio: data.bio,
      };

      const response = await userAPI.updateProfile(updateData);
      const updatedUser = response.data.data;

      // AuthContextì˜ ì‚¬ìš©ì ì •ë³´ ì—…ë°ì´íŠ¸
      await updateUser(updatedUser);

      Alert.alert(
        'í”„ë¡œí•„ ìˆ˜ì • ì™„ë£Œ',
        'í”„ë¡œí•„ì´ ì„±ê³µì ìœ¼ë¡œ ìˆ˜ì •ë˜ì—ˆìŠµë‹ˆë‹¤.',
        [
          {
            text: 'í™•ì¸',
            onPress: () => navigation.goBack()
          }
        ]
      );
    } catch (error) {
      console.error('í”„ë¡œí•„ ìˆ˜ì • ì˜¤ë¥˜:', error);
      Alert.alert(
        'ìˆ˜ì • ì‹¤íŒ¨',
        error.response?.data?.error?.message || 'í”„ë¡œí•„ ìˆ˜ì • ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.'
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleImagePicker = () => {
    Alert.alert(
      'í”„ë¡œí•„ ì´ë¯¸ì§€ ë³€ê²½',
      'ì´ë¯¸ì§€ë¥¼ ì„ íƒí•˜ëŠ” ë°©ë²•ì„ ì„ íƒí•´ì£¼ì„¸ìš”.',
      [
        { text: 'ì·¨ì†Œ', style: 'cancel' },
        { text: 'ê°¤ëŸ¬ë¦¬ì—ì„œ ì„ íƒ', onPress: pickImageFromGallery },
        { text: 'ì¹´ë©”ë¼ë¡œ ì´¬ì˜', onPress: pickImageFromCamera }
      ]
    );
  };

  const pickImageFromGallery = async () => {
    try {
      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [1, 1],
        quality: 0.8,
      });

      if (!result.canceled) {
        await uploadProfileImage(result.assets[0]);
      }
    } catch (error) {
      Alert.alert('ì˜¤ë¥˜', 'ì´ë¯¸ì§€ ì„ íƒ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    }
  };

  const pickImageFromCamera = async () => {
    try {
      const { status } = await ImagePicker.requestCameraPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('ê¶Œí•œ í•„ìš”', 'ì¹´ë©”ë¼ ì‚¬ìš©ì„ ìœ„í•´ ê¶Œí•œì´ í•„ìš”í•©ë‹ˆë‹¤.');
        return;
      }

      const result = await ImagePicker.launchCameraAsync({
        allowsEditing: true,
        aspect: [1, 1],
        quality: 0.8,
      });

      if (!result.canceled) {
        await uploadProfileImage(result.assets[0]);
      }
    } catch (error) {
      Alert.alert('ì˜¤ë¥˜', 'ì¹´ë©”ë¼ ì‚¬ìš© ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    }
  };

  const uploadProfileImage = async (imageAsset) => {
    try {
      setIsImageUploading(true);

      const imageData = {
        uri: imageAsset.uri,
        type: 'image/jpeg',
        fileName: 'profile.jpg',
      };

      const response = await userAPI.uploadProfileImage(imageData);
      const imageUrl = response.data.data.imageUrl;

      setProfileImage(imageUrl);

      // ì‚¬ìš©ì ì •ë³´ ì—…ë°ì´íŠ¸
      const updatedUser = { ...user, profileImage: imageUrl };
      await updateUser(updatedUser);

      Alert.alert('ì„±ê³µ', 'í”„ë¡œí•„ ì´ë¯¸ì§€ê°€ ë³€ê²½ë˜ì—ˆìŠµë‹ˆë‹¤.');
    } catch (error) {
      console.error('ì´ë¯¸ì§€ ì—…ë¡œë“œ ì˜¤ë¥˜:', error);
      Alert.alert('ì—…ë¡œë“œ ì‹¤íŒ¨', 'ì´ë¯¸ì§€ ì—…ë¡œë“œ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    } finally {
      setIsImageUploading(false);
    }
  };

  const getLevelText = (level) => {
    switch (level) {
      case 'beginner': return 'ì´ˆê¸‰';
      case 'intermediate': return 'ì¤‘ê¸‰';
      case 'advanced': return 'ê³ ê¸‰';
      case 'expert': return 'ì „ë¬¸ê°€';
      default: return 'ì´ˆê¸‰';
    }
  };

  const renderProfileImage = () => (
    <Card style={styles.imageCard}>
      <Card.Content>
        <View style={styles.imageContainer}>
          <View style={styles.avatarContainer}>
            <Avatar.Image
              size={120}
              source={{ uri: profileImage || 'https://via.placeholder.com/120' }}
              style={styles.avatar}
            />
            {isImageUploading && (
              <View style={styles.uploadingOverlay}>
                <ActivityIndicator size="large" color={theme.colors.surface} />
              </View>
            )}
            <IconButton
              icon="camera"
              size={24}
              onPress={handleImagePicker}
              disabled={isImageUploading}
              style={styles.cameraButton}
              iconColor={theme.colors.surface}
            />
          </View>
          <Text style={styles.imageDescription}>
            í”„ë¡œí•„ ì‚¬ì§„ì„ í„°ì¹˜í•˜ì—¬ ë³€ê²½í•˜ì„¸ìš”
          </Text>
        </View>
      </Card.Content>
    </Card>
  );

  const renderBasicInfo = () => (
    <Card style={styles.sectionCard}>
      <Card.Content>
        <Text style={styles.sectionTitle}>ê¸°ë³¸ ì •ë³´</Text>

        {/* ì´ë¦„ */}
        <Controller
          control={control}
          name="name"
          rules={{
            required: 'ì´ë¦„ì„ ì…ë ¥í•´ì£¼ì„¸ìš”',
            minLength: {
              value: 2,
              message: 'ì´ë¦„ì€ 2ì ì´ìƒì´ì–´ì•¼ í•©ë‹ˆë‹¤',
            },
          }}
          render={({ field: { onChange, onBlur, value } }) => (
            <TextInput
              mode="outlined"
              label="ì´ë¦„"
              placeholder="í™ê¸¸ë™"
              value={value}
              onBlur={onBlur}
              onChangeText={onChange}
              error={!!errors.name}
              style={styles.input}
              left={<TextInput.Icon icon="account" />}
            />
          )}
        />
        {errors.name && (
          <Text style={styles.errorText}>{errors.name.message}</Text>
        )}

        {/* ì´ë©”ì¼ (ì½ê¸° ì „ìš©) */}
        <Controller
          control={control}
          name="email"
          render={({ field: { value } }) => (
            <TextInput
              mode="outlined"
              label="ì´ë©”ì¼"
              value={value}
              editable={false}
              style={[styles.input, styles.disabledInput]}
              left={<TextInput.Icon icon="email" />}
              right={<TextInput.Icon icon="lock" />}
            />
          )}
        />
        <Text style={styles.helperText}>ì´ë©”ì¼ì€ ë³€ê²½í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤</Text>

        {/* ì „í™”ë²ˆí˜¸ */}
        <Controller
          control={control}
          name="phone"
          rules={{
            pattern: {
              value: /^010-\d{4}-\d{4}$/,
              message: 'ì „í™”ë²ˆí˜¸ í˜•ì‹: 010-1234-5678',
            },
          }}
          render={({ field: { onChange, onBlur, value } }) => (
            <TextInput
              mode="outlined"
              label="ì „í™”ë²ˆí˜¸"
