import React, { useState, useEffect, useRef } from 'react';
import { View, StyleSheet, FlatList, KeyboardAvoidingView, Platform } from 'react-native';
import { 
  Text, 
  TextInput,
  Avatar,
  Surface,
  IconButton,
  ActivityIndicator,
  Chip
} from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useAuth } from '../../context/AuthContext';
import { useSocket } from '../../context/SocketContext';
import { chatAPI } from '../../services/api';
import { theme } from '../../utils/theme';

const ChatScreen = ({ route, navigation }) => {
  const { roomId, roomName, roomType } = route.params;
  const { user } = useAuth();
  const { socket } = useSocket();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isSending, setIsSending] = useState(false);
  const flatListRef = useRef(null);

  useEffect(() => {
    navigation.setOptions({
      title: roomName || 'ì±„íŒ…',
    });

    loadMessages();
    joinChatRoom();

    return () => {
      leaveChatRoom();
    };
  }, [roomId]);

  useEffect(() => {
    if (socket) {
      socket.on('chat:message', handleNewMessage);
      socket.on('chat:user_joined', handleUserJoined);
      socket.on('chat:user_left', handleUserLeft);

      return () => {
        socket.off('chat:message');
        socket.off('chat:user_joined');
        socket.off('chat:user_left');
      };
    }
  }, [socket]);

  const loadMessages = async () => {
    try {
      setIsLoading(true);
      const response = await chatAPI.getMessages(roomId);
      setMessages(response.data.data || []);
    } catch (error) {
      console.error('ë©”ì‹œì§€ ë¡œë“œ ì˜¤ë¥˜:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const joinChatRoom = () => {
    if (socket) {
      socket.emit('join_chat_room', { roomId, roomType });
    }
  };

  const leaveChatRoom = () => {
    if (socket) {
      socket.emit('leave_chat_room', { roomId });
    }
  };

  const handleNewMessage = (message) => {
    setMessages(prev => [...prev, message]);
    scrollToBottom();
  };

  const handleUserJoined = (data) => {
    const systemMessage = {
      _id: `system_${Date.now()}`,
      type: 'system',
      content: `${data.user.name}ë‹˜ì´ ì…ì¥í–ˆìŠµë‹ˆë‹¤.`,
      createdAt: new Date().toISOString(),
    };
    setMessages(prev => [...prev, systemMessage]);
  };

  const handleUserLeft = (data) => {
    const systemMessage = {
      _id: `system_${Date.now()}`,
      type: 'system',
      content: `${data.user.name}ë‹˜ì´ í‡´ì¥í–ˆìŠµë‹ˆë‹¤.`,
      createdAt: new Date().toISOString(),
    };
    setMessages(prev => [...prev, systemMessage]);
  };

  const sendMessage = async () => {
    if (!newMessage.trim() || isSending) return;

    try {
      setIsSending(true);
      const messageData = {
        roomId,
        content: newMessage.trim(),
        type: 'text',
      };

      await chatAPI.sendMessage(messageData);
      setNewMessage('');
    } catch (error) {
      console.error('ë©”ì‹œì§€ ì „ì†¡ ì˜¤ë¥˜:', error);
    } finally {
      setIsSending(false);
    }
  };

  const scrollToBottom = () => {
    setTimeout(() => {
      flatListRef.current?.scrollToEnd({ animated: true });
    }, 100);
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ko-KR', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const today = new Date();

    if (date.toDateString() === today.toDateString()) {
      return 'ì˜¤ëŠ˜';
    }

    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === yesterday.toDateString()) {
      return 'ì–´ì œ';
    }

    return date.toLocaleDateString('ko-KR', {
      month: 'long',
      day: 'numeric',
    });
  };

  const renderDateSeparator = (date) => (
    <View style={styles.dateSeparator}>
      <View style={styles.dateLine} />
      <Chip mode="outlined" style={styles.dateChip}>
        {formatDate(date)}
      </Chip>
      <View style={styles.dateLine} />
    </View>
  );

  const renderSystemMessage = (message) => (
    <View style={styles.systemMessageContainer}>
      <Text style={styles.systemMessage}>{message.content}</Text>
    </View>
  );

  const renderUserMessage = (message) => {
    const isMyMessage = message.sender._id === user?.id;

    return (
      <View style={[
        styles.messageContainer,
        isMyMessage ? styles.myMessageContainer : styles.otherMessageContainer
      ]}>
        {!isMyMessage && (
          <Avatar.Image
            size={36}
            source={{ uri: message.sender.profileImage || 'https://via.placeholder.com/36' }}
            style={styles.avatar}
          />
        )}

        <View style={[
          styles.messageContent,
          isMyMessage ? styles.myMessageContent : styles.otherMessageContent
        ]}>
          {!isMyMessage && (
            <Text style={styles.senderName}>{message.sender.name}</Text>
          )}

          <Surface style={[
            styles.messageBubble,
            isMyMessage ? styles.myMessageBubble : styles.otherMessageBubble
          ]} elevation={1}>
            <Text style={[
              styles.messageText,
              isMyMessage ? styles.myMessageText : styles.otherMessageText
            ]}>
              {message.content}
            </Text>
          </Surface>

          <Text style={[
            styles.messageTime,
            isMyMessage ? styles.myMessageTime : styles.otherMessageTime
          ]}>
            {formatTime(message.createdAt)}
          </Text>
        </View>
      </View>
    );
  };

  const renderMessage = ({ item: message, index }) => {
    const showDateSeparator = index === 0 || 
      new Date(message.createdAt).toDateString() !== 
      new Date(messages[index - 1]?.createdAt).toDateString();

    return (
      <View>
        {showDateSeparator && renderDateSeparator(message.createdAt)}

        {message.type === 'system' 
          ? renderSystemMessage(message)
          : renderUserMessage(message)
        }
      </View>
    );
  };

  if (isLoading) {
    return (
      <View style={styles.loadingContainer}>
        <ActivityIndicator size="large" color={theme.colors.primary} />
        <Text style={styles.loadingText}>ì±„íŒ…ì„ ë¶ˆëŸ¬ì˜¤ëŠ” ì¤‘...</Text>
      </View>
    );
  }

  return (
    <SafeAreaView style={styles.container} edges={['bottom']}>
      <KeyboardAvoidingView 
        style={styles.keyboardAvoidingView}
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      >
        <FlatList
          ref={flatListRef}
          data={messages}
          keyExtractor={(item) => item._id}
          renderItem={renderMessage}
          style={styles.messagesList}
          contentContainerStyle={styles.messagesContent}
          onContentSizeChange={scrollToBottom}
          showsVerticalScrollIndicator={false}
        />

        <View style={styles.inputContainer}>
          <TextInput
            mode="outlined"
            placeholder="ë©”ì‹œì§€ë¥¼ ì…ë ¥í•˜ì„¸ìš”..."
            value={newMessage}
            onChangeText={setNewMessage}
            multiline
            maxLength={500}
            style={styles.textInput}
            contentStyle={styles.textInputContent}
            onSubmitEditing={sendMessage}
            returnKeyType="send"
          />
          <IconButton
            icon="send"
            size={24}
            onPress={sendMessage}
            disabled={!newMessage.trim() || isSending}
            style={[
              styles.sendButton,
              (!newMessage.trim() || isSending) && styles.sendButtonDisabled
            ]}
            iconColor={
              !newMessage.trim() || isSending 
                ? theme.colors.outline 
                : theme.colors.surface
            }
          />
        </View>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
