import React, { createContext, useContext, useReducer, useEffect } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { authAPI, userAPI } from '../services/api';
import { showMessage } from 'react-native-flash-message';

// ì¸ì¦ ìƒíƒœ ê´€ë¦¬
const AuthContext = createContext();

// ì´ˆê¸° ìƒíƒœ
const initialState = {
  isLoading: true,
  isAuthenticated: false,
  user: null,
  token: null,
  refreshToken: null,
  error: null,
};

// ì•¡ì…˜ íƒ€ì…
const AUTH_ACTIONS = {
  SET_LOADING: 'SET_LOADING',
  LOGIN_SUCCESS: 'LOGIN_SUCCESS',
  LOGIN_FAILURE: 'LOGIN_FAILURE',
  LOGOUT: 'LOGOUT',
  UPDATE_USER: 'UPDATE_USER',
  CLEAR_ERROR: 'CLEAR_ERROR',
  SET_ERROR: 'SET_ERROR',
};

// ë¦¬ë“€ì„œ
const authReducer = (state, action) => {
  switch (action.type) {
    case AUTH_ACTIONS.SET_LOADING:
      return {
        ...state,
        isLoading: action.payload,
      };

    case AUTH_ACTIONS.LOGIN_SUCCESS:
      return {
        ...state,
        isLoading: false,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        refreshToken: action.payload.refreshToken,
        error: null,
      };

    case AUTH_ACTIONS.LOGIN_FAILURE:
      return {
        ...state,
        isLoading: false,
        isAuthenticated: false,
        user: null,
        token: null,
        refreshToken: null,
        error: action.payload,
      };

    case AUTH_ACTIONS.LOGOUT:
      return {
        ...state,
        isLoading: false,
        isAuthenticated: false,
        user: null,
        token: null,
        refreshToken: null,
        error: null,
      };

    case AUTH_ACTIONS.UPDATE_USER:
      return {
        ...state,
        user: { ...state.user, ...action.payload },
      };

    case AUTH_ACTIONS.CLEAR_ERROR:
      return {
        ...state,
        error: null,
      };

    case AUTH_ACTIONS.SET_ERROR:
      return {
        ...state,
        error: action.payload,
      };

    default:
      return state;
  }
};

// ì¸ì¦ í”„ë¡œë°”ì´ë”
export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // ì•± ì‹œì‘ ì‹œ ì €ì¥ëœ í† í° í™•ì¸
  useEffect(() => {
    checkAuthStatus();
  }, []);

  const checkAuthStatus = async () => {
    try {
      dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: true });

      const [token, refreshToken, userInfo] = await AsyncStorage.multiGet([
        'authToken',
        'refreshToken',
        'userInfo'
      ]);

      const authToken = token[1];
      const refresh = refreshToken[1];
      const user = userInfo[1] ? JSON.parse(userInfo[1]) : null;

      if (authToken && user) {
        // í† í°ì´ ìˆìœ¼ë©´ ì‚¬ìš©ì ì •ë³´ ê°±ì‹ 
        try {
          const response = await userAPI.getProfile();
          const updatedUser = response.data.data.user;

          dispatch({
            type: AUTH_ACTIONS.LOGIN_SUCCESS,
            payload: {
              token: authToken,
              refreshToken: refresh,
              user: updatedUser,
            },
          });

          // ì—…ë°ì´íŠ¸ëœ ì‚¬ìš©ì ì •ë³´ ì €ì¥
          await AsyncStorage.setItem('userInfo', JSON.stringify(updatedUser));
        } catch (error) {
          // í† í°ì´ ìœ íš¨í•˜ì§€ ì•Šìœ¼ë©´ ë¡œê·¸ì•„ì›ƒ
          await logout();
        }
      } else {
        dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: false });
      }
    } catch (error) {
      console.error('ì¸ì¦ ìƒíƒœ í™•ì¸ ì˜¤ë¥˜:', error);
      dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: false });
    }
  };

  const login = async (credentials) => {
    try {
      dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: true });
      dispatch({ type: AUTH_ACTIONS.CLEAR_ERROR });

      const response = await authAPI.login(credentials);
      const { user, accessToken, refreshToken } = response.data.data;

      // í† í°ê³¼ ì‚¬ìš©ì ì •ë³´ ì €ì¥
      await AsyncStorage.multiSet([
        ['authToken', accessToken],
        ['refreshToken', refreshToken],
        ['userInfo', JSON.stringify(user)],
      ]);

      dispatch({
        type: AUTH_ACTIONS.LOGIN_SUCCESS,
        payload: {
          token: accessToken,
          refreshToken,
          user,
        },
      });

      showMessage({
        message: 'ë¡œê·¸ì¸ ì„±ê³µ',
        description: `${user.name}ë‹˜, í™˜ì˜í•©ë‹ˆë‹¤!`,
        type: 'success',
      });

      return { success: true };
    } catch (error) {
      const errorMessage = error.response?.data?.error?.message || 'ë¡œê·¸ì¸ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.';

      dispatch({
        type: AUTH_ACTIONS.LOGIN_FAILURE,
        payload: errorMessage,
      });

      showMessage({
        message: 'ë¡œê·¸ì¸ ì‹¤íŒ¨',
        description: errorMessage,
        type: 'danger',
      });

      return { success: false, error: errorMessage };
    }
  };

  const signup = async (userData) => {
    try {
      dispatch({ type: AUTH_ACTIONS.SET_LOADING, payload: true });
      dispatch({ type: AUTH_ACTIONS.CLEAR_ERROR });

      const response = await authAPI.signup(userData);
      const { user, accessToken, refreshToken } = response.data.data;

      // í† í°ê³¼ ì‚¬ìš©ì ì •ë³´ ì €ì¥
      await AsyncStorage.multiSet([
        ['authToken', accessToken],
        ['refreshToken', refreshToken],
        ['userInfo', JSON.stringify(user)],
      ]);

      dispatch({
        type: AUTH_ACTIONS.LOGIN_SUCCESS,
        payload: {
          token: accessToken,
          refreshToken,
          user,
        },
      });

      showMessage({
        message: 'íšŒì›ê°€ì… ì„±ê³µ',
        description: `${user.name}ë‹˜, ë™ë°°ì¦ì— ì˜¤ì‹  ê²ƒì„ í™˜ì˜í•©ë‹ˆë‹¤!`,
        type: 'success',
      });

      return { success: true };
    } catch (error) {
      const errorMessage = error.response?.data?.error?.message || 'íšŒì›ê°€ì…ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.';

      dispatch({
        type: AUTH_ACTIONS.SET_ERROR,
        payload: errorMessage,
      });

      showMessage({
        message: 'íšŒì›ê°€ì… ì‹¤íŒ¨',
        description: errorMessage,
        type: 'danger',
      });

      return { success: false, error: errorMessage };
    }
  };

  const logout = async () => {
    try {
      // ì„œë²„ì— ë¡œê·¸ì•„ì›ƒ ìš”ì²­
      if (state.isAuthenticated) {
        await authAPI.logout();
      }
    } catch (error) {
      console.error('ë¡œê·¸ì•„ì›ƒ ìš”ì²­ ì˜¤ë¥˜:', error);
    } finally {
      // ë¡œì»¬ ì €ì¥ì†Œ ì •ë¦¬
      await AsyncStorage.multiRemove(['authToken', 'refreshToken', 'userInfo']);

      dispatch({ type: AUTH_ACTIONS.LOGOUT });

      showMessage({
        message: 'ë¡œê·¸ì•„ì›ƒ',
        description: 'ì•ˆì „í•˜ê²Œ ë¡œê·¸ì•„ì›ƒë˜ì—ˆìŠµë‹ˆë‹¤.',
        type: 'info',
      });
    }
  };

  const updateProfile = async (userData) => {
    try {
      const response = await userAPI.updateProfile(userData);
      const updatedUser = response.data.data.user;

      // ì»¨í…ìŠ¤íŠ¸ ìƒíƒœ ì—…ë°ì´íŠ¸
      dispatch({
        type: AUTH_ACTIONS.UPDATE_USER,
        payload: updatedUser,
      });

      // ë¡œì»¬ ì €ì¥ì†Œ ì—…ë°ì´íŠ¸
      await AsyncStorage.setItem('userInfo', JSON.stringify({
        ...state.user,
        ...updatedUser,
      }));

      showMessage({
        message: 'í”„ë¡œí•„ ì—…ë°ì´íŠ¸',
        description: 'í”„ë¡œí•„ì´ ì„±ê³µì ìœ¼ë¡œ ì—…ë°ì´íŠ¸ë˜ì—ˆìŠµë‹ˆë‹¤.',
        type: 'success',
      });

      return { success: true, user: updatedUser };
    } catch (error) {
      const errorMessage = error.response?.data?.error?.message || 'í”„ë¡œí•„ ì—…ë°ì´íŠ¸ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.';

      showMessage({
        message: 'í”„ë¡œí•„ ì—…ë°ì´íŠ¸ ì‹¤íŒ¨',
        description: errorMessage,
        type: 'danger',
      });

      return { success: false, error: errorMessage };
    }
  };

  const uploadProfileImage = async (imageData) => {
    try {
      const response = await userAPI.uploadProfileImage(imageData);
      const { profileImage } = response.data.data;

      // ì‚¬ìš©ì ì •ë³´ ì—…ë°ì´íŠ¸
      const updatedUser = { ...state.user, profileImage };

      dispatch({
        type: AUTH_ACTIONS.UPDATE_USER,
        payload: { profileImage },
      });

      await AsyncStorage.setItem('userInfo', JSON.stringify(updatedUser));

      showMessage({
        message: 'í”„ë¡œí•„ ì‚¬ì§„ ì—…ë°ì´íŠ¸',
        description: 'í”„ë¡œí•„ ì‚¬ì§„ì´ ì„±ê³µì ìœ¼ë¡œ ì—…ë°ì´íŠ¸ë˜ì—ˆìŠµë‹ˆë‹¤.',
        type: 'success',
      });

      return { success: true, profileImage };
    } catch (error) {
      const errorMessage = error.response?.data?.error?.message || 'í”„ë¡œí•„ ì‚¬ì§„ ì—…ë¡œë“œì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.';

      showMessage({
        message: 'í”„ë¡œí•„ ì‚¬ì§„ ì—…ë¡œë“œ ì‹¤íŒ¨',
        description: errorMessage,
        type: 'danger',
      });

      return { success: false, error: errorMessage };
    }
  };

  const requestPasswordReset = async (email) => {
    try {
      await authAPI.requestPasswordReset(email);

      showMessage({
        message: 'ë¹„ë°€ë²ˆí˜¸ ì¬ì„¤ì •',
        description: 'ì´ë©”ì¼ë¡œ ì¬ì„¤ì • ë§í¬ë¥¼ ë°œì†¡í–ˆìŠµë‹ˆë‹¤.',
        type: 'success',
      });

      return { success: true };
    } catch (error) {
      const errorMessage = error.response?.data?.error?.message || 'ë¹„ë°€ë²ˆí˜¸ ì¬ì„¤ì • ìš”ì²­ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.';

      showMessage({
        message: 'ë¹„ë°€ë²ˆí˜¸ ì¬ì„¤ì • ì‹¤íŒ¨',
        description: errorMessage,
        type: 'danger',
      });

      return { success: false, error: errorMessage };
    }
  };

  const resetPassword = async (token, newPassword) => {
    try {
      await authAPI.resetPassword(token, newPassword);

      showMessage({
        message: 'ë¹„ë°€ë²ˆí˜¸ ì¬ì„¤ì • ì™„ë£Œ',
        description: 'ìƒˆ ë¹„ë°€ë²ˆí˜¸ë¡œ ë¡œê·¸ì¸í•´ì£¼ì„¸ìš”.',
        type: 'success',
      });

      return { success: true };
    } catch (error) {
      const errorMessage = error.response?.data?.error?.message || 'ë¹„ë°€ë²ˆí˜¸ ì¬ì„¤ì •ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.';

      showMessage({
        message: 'ë¹„ë°€ë²ˆí˜¸ ì¬ì„¤ì • ì‹¤íŒ¨',
        description: errorMessage,
        type: 'danger',
      });

      return { success: false, error: errorMessage };
    }
  };

  const clearError = () => {
    dispatch({ type: AUTH_ACTIONS.CLEAR_ERROR });
  };

  const value = {
    ...state,
    login,
    signup,
    logout,
    updateProfile,
    uploadProfileImage,
    requestPasswordReset,
    resetPassword,
    clearError,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

// ì¸ì¦ ì»¨í…ìŠ¤íŠ¸ ì‚¬ìš© í›…
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export default AuthContext;

