import React, { createContext, useContext, useReducer, useEffect } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { authAPI, userAPI } from '../services/api';
import bandAPI from '../services/bandAPI';
import { showMessage } from 'react-native-flash-message';

// ì¸ì¦ ìƒíƒœ ê´€ë¦¬
const AuthContext = createContext();

// ì´ˆê¸° ìƒíƒœ
const initialState = {
  isLoading: true,
  isAuthenticated: false,
  user: null,
  token: null,
  refreshToken: null,
  error: null,
  // Band ê´€ë ¨ ìƒíƒœ
  isBandUser: false,
  bandAccessToken: null,
  selectedBand: null,
};

// ì•¡ì…˜ íƒ€ì…
const AUTH_ACTIONS = {
  SET_LOADING: 'SET_LOADING',
  LOGIN_SUCCESS: 'LOGIN_SUCCESS',
  LOGIN_FAILURE: 'LOGIN_FAILURE',
  LOGOUT: 'LOGOUT',
  UPDATE_USER: 'UPDATE_USER',
  CLEAR_ERROR: 'CLEAR_ERROR',
  SET_ERROR: 'SET_ERROR',
  // Band ê´€ë ¨ ì•¡ì…˜
  BAND_LOGIN_SUCCESS: 'BAND_LOGIN_SUCCESS',
  SET_SELECTED_BAND: 'SET_SELECTED_BAND',
};

// ë¦¬ë“€ì„œ
const authReducer = (state, action) => {
  switch (action.type) {
    case AUTH_ACTIONS.SET_LOADING:
      return {
        ...state,
        isLoading: action.payload,
      };

    case AUTH_ACTIONS.LOGIN_SUCCESS:
      return {
        ...state,
        isLoading: false,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        refreshToken: action.payload.refreshToken,
        error: null,
      };

    case AUTH_ACTIONS.LOGIN_FAILURE:
      return {
        ...state,
        isLoading: false,
        isAuthenticated: false,
        user: null,
        token: null,
        refreshToken: null,
        error: action.payload,
      };

    case AUTH_ACTIONS.LOGOUT:
      return {
        ...state,
        isLoading: false,
        isAuthenticated: false,
        user: null,
        token: null,
        refreshToken: null,
        error: null,
      };

    case AUTH_ACTIONS.UPDATE_USER:
      return {
        ...state,
        user: { ...state.user, ...action.payload },
      };

    case AUTH_ACTIONS.CLEAR_ERROR:
      return {
        ...state,
        error: null,
      };

    case AUTH_ACTIONS.SET_ERROR:
      return {
        ...state,
        error: action.payload,
      };

    case AUTH_ACTIONS.BAND_LOGIN_SUCCESS:
      return {
        ...state,
        isLoading: false,
        isAuthenticated: true,
        isBandUser: true,
        user: action.payload.user,
        bandAccessToken: action.payload.accessToken,
        error: null,
      };

    case AUTH_ACTIONS.SET_SELECTED_BAND:
      return {
        ...state,
        selectedBand: action.payload,
      };

    default:
      return state;
  }
};

