    />
  </Stack.Navigator>
);

// í”„ë¡œí•„ ìŠ¤íƒ
const ProfileStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="ProfileMain" 
      component={ProfileScreen}
      options={{ headerShown: false }}
    />
