import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';

import theme from '../utils/theme';

// ìŠ¤í¬ë¦° ì„í¬íŠ¸ - ë™í˜¸íšŒ ì „ìš©
import ClubHomeScreen from '../screens/main/ClubHomeScreen'; // ê¸°ì¡´ HomeScreen ëŒ€ì²´
import MembersScreen from '../screens/main/MembersScreen'; // ê¸°ì¡´ ClubsScreen ëŒ€ì²´
import GamesScreen from '../screens/main/GamesScreen';
import ProfileScreen from '../screens/main/ProfileScreen';
import NotificationsScreen from '../screens/detail/NotificationsScreen';
import PaymentScreen from '../screens/club/PaymentScreen';

// ìƒì„¸ ìŠ¤í¬ë¦°ë“¤
import ClubDetailScreen from '../screens/detail/ClubDetailScreen';
import ClubCreateScreen from '../screens/detail/ClubCreateScreen';
import GameDetailScreen from '../screens/detail/GameDetailScreen';
import GameCreateScreen from '../screens/detail/GameCreateScreen';
import ProfileEditScreen from '../screens/detail/ProfileEditScreen';
import StatisticsScreen from '../screens/detail/StatisticsScreen';
import RankingScreen from '../screens/detail/RankingScreen';
import ChatScreen from '../screens/detail/ChatScreen';

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

// ë™í˜¸íšŒ í™ˆ ìŠ¤íƒ
const HomeStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="HomeMain" 
      component={ClubHomeScreen}
      options={{ headerShown: false }}
    />
    <Stack.Screen 
      name="Notifications" 
      component={NotificationsScreen}
      options={{ 
        title: 'ì•Œë¦¼',
        headerStyle: { backgroundColor: theme.colors.primary },
        headerTintColor: '#fff',
      }}
    />
    <Stack.Screen 
      name="Payment" 
      component={PaymentScreen}
      options={{ 
        title: 'ëª¨ì„ë¹„ ë‚©ë¶€',
        headerStyle: { backgroundColor: theme.colors.primary },
        headerTintColor: '#fff',
      }}
    />
  </Stack.Navigator>
);

// ë©¤ë²„ ìŠ¤íƒ - ë™í˜¸íšŒ ì „ìš©
const MembersStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="MembersMain" 
      component={MembersScreen}
      options={{ headerShown: false }}
    />
    <Stack.Screen 
      name="MemberProfile" 
      component={ProfileScreen} // ì„ì‹œë¡œ ê¸°ì¡´ í”„ë¡œí•„ í™”ë©´ ì‚¬ìš©
      options={{ 
        title: 'ë©¤ë²„ í”„ë¡œí•„',
        headerStyle: { backgroundColor: theme.colors.primary },
        headerTintColor: '#fff',
      }}
    />
    <Stack.Screen 
      name="Chat" 
      component={ChatScreen}
      options={({ route }) => ({ 
        title: route.params?.title || 'ì±„íŒ…',
        headerStyle: { backgroundColor: theme.colors.primary },
        headerTintColor: '#fff',
      })}
    />
  </Stack.Navigator>
);

// ê²Œì„ ìŠ¤íƒ
const GamesStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="GamesMain" 
      component={GamesScreen}
      options={{ headerShown: false }}
    />
    <Stack.Screen 
      name="GameDetail" 
      component={GameDetailScreen}
      options={{ 
        title: 'ê²Œì„ ìƒì„¸',
        headerStyle: { backgroundColor: theme.colors.primary },
        headerTintColor: '#fff',
      }}
    />
    <Stack.Screen 
      name="GameCreate" 
      component={GameCreateScreen}
      options={{ 
        title: 'ê²Œì„ ë§Œë“¤ê¸°',
        headerStyle: { backgroundColor: theme.colors.primary },
        headerTintColor: '#fff',
      }}
    />
  </Stack.Navigator>
);

// í”„ë¡œí•„ ìŠ¤íƒ
const ProfileStack = () => (
  <Stack.Navigator>
    <Stack.Screen 
      name="ProfileMain" 
      component={ProfileScreen}
      options={{ headerShown: false }}
    />
    <Stack.Screen 
      name="ProfileEdit" 
      component={ProfileEditScreen}
      options={{ 
        title: 'í”„ë¡œí•„ ìˆ˜ì •',
        headerStyle: { backgroundColor: theme.colors.primary },
        headerTintColor: '#fff',
      }}
    />
    <Stack.Screen 
      name="Statistics" 
      component={StatisticsScreen}
      options={{ 
        title: 'ë‚´ í†µê³„',
        headerStyle: { backgroundColor: theme.colors.primary },
        headerTintColor: '#fff',
      }}
    />
    <Stack.Screen 
      name="Ranking" 
      component={RankingScreen}
      options={{ 
        title: 'ë­í‚¹',
        headerStyle: { backgroundColor: theme.colors.primary },
        headerTintColor: '#fff',
      }}
    />
  </Stack.Navigator>
);

const MainNavigator = () => {
  return (
    <Tab.Navigator
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          switch (route.name) {
            case 'Home':
              iconName = focused ? 'home' : 'home-outline';
              break;
            case 'Members':
              iconName = focused ? 'account-group' : 'account-group-outline';
              break;
            case 'Games':
              iconName = focused ? 'badminton' : 'badminton';
              break;
            case 'Profile':
              iconName = focused ? 'account' : 'account-outline';
              break;
            default:
              iconName = 'circle';
          }

          return <MaterialCommunityIcons name={iconName} size={size} color={color} />;
        },
        tabBarActiveTintColor: theme.colors.primary,
        tabBarInactiveTintColor: theme.colors.placeholder,
        tabBarStyle: {
          backgroundColor: '#fff',
          borderTopWidth: 1,
          borderTopColor: '#E0E0E0',
          paddingBottom: 5,
          height: 60,
        },
        tabBarLabelStyle: {
          fontSize: 12,
          fontWeight: '500',
        },
        headerShown: false,
      })}
    >
      <Tab.Screen 
        name="Home" 
        component={HomeStack}
        options={{ tabBarLabel: 'í™ˆ' }}
      />
      <Tab.Screen 
        name="Members" 
        component={MembersStack}
        options={{ tabBarLabel: 'ë©¤ë²„' }}
      />
      <Tab.Screen 
        name="Games" 
        component={GamesStack}
        options={{ tabBarLabel: 'ê²Œì„' }}
      />
      <Tab.Screen 
        name="Profile" 
        component={ProfileStack}
        options={{ tabBarLabel: 'í”„ë¡œí•„' }}
      />
    </Tab.Navigator>
  );
};

export default MainNavigator;

import React, { useState, useEffect } from 'react';
import { View, StyleSheet, ScrollView, RefreshControl } from 'react-native';
import { 
  Text, 
  Card, 
  Avatar, 
  Button, 
  Chip, 
  Surface,
  IconButton,
  ActivityIndicator,
  List,
  Divider,
  Badge
} from 'react-native-paper';
import { SafeAreaView } from 'react-native-safe-area-context';
import { useAuth } from '../../context/AuthContext';
import { useSocket } from '../../context/SocketContext';
import { gameAPI, bandAPI } from '../../services/api';
import theme from '../../utils/theme';

/**
 * ë°´ë“œ ë™í˜¸íšŒ ì „ìš© í™ˆ í™”ë©´
 * - ë‹¨ì¼ ë°´ë“œ ê¸°ë°˜ íì‡„í˜• êµ¬ì¡°
 * - ë™í˜¸íšŒ ë©¤ë²„ë“¤ë§Œì˜ ê²Œì„ ê´€ë¦¬
 * - ì°¸ì„ë¹„, ê³µì§€ì‚¬í•­, ê²Œì„ ë°ì´í„° ì¤‘ì‹¬
 */
const ClubHomeScreen = ({ navigation }) => {
  const { user, selectedBand } = useAuth();
  const { notifications } = useSocket();
  const [isLoading, setIsLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [clubData, setClubData] = useState({
    upcomingGames: [],
    recentNotices: [],
    memberStats: null,
    paymentStatus: null,
    clubMembers: [],
  });

  useEffect(() => {
    if (selectedBand) {
      loadClubData();
    }
  }, [selectedBand]);

  const loadClubData = async () => {
    try {
      setIsLoading(true);

      // ë°´ë“œ ê¸°ë°˜ ë°ì´í„°ë§Œ ë¡œë“œ
      const [
        upcomingGamesRes,
        bandMembers,
        bandNotices
      ] = await Promise.all([
        gameAPI.getClubGames(selectedBand.id, { status: 'upcoming', limit: 3 }),
        bandAPI.getBandMembers(selectedBand.bandKey),
        bandAPI.getBandPosts(selectedBand.bandKey, null, 5)
      ]);

      setClubData({
        upcomingGames: upcomingGamesRes.data?.data || [],
        clubMembers: bandMembers || [],
        recentNotices: bandNotices?.posts || [],
        memberStats: {
          totalMembers: bandMembers.length,
          activeMembers: bandMembers.filter(m => m.lastActive > Date.now() - 7*24*60*60*1000).length,
          thisMonthGames: 3, // Mock data
        },
        paymentStatus: {
          paid: true,
          amount: 50000,
          dueDate: '2024-02-15',
          method: 'ì •ê¸° ëª¨ì„ë¹„'
        }
      });
    } catch (error) {
      console.error('ë™í˜¸íšŒ ë°ì´í„° ë¡œë“œ ì˜¤ë¥˜:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const onRefresh = async () => {
    setRefreshing(true);
    await loadClubData();
    setRefreshing(false);
  };

  const renderClubHeader = () => (
    <Card style={styles.clubCard}>
      <Card.Content>
        <View style={styles.clubHeader}>
          <Avatar.Image 
            size={60} 
            source={{ uri: selectedBand?.clubImage || 'https://via.placeholder.com/120' }}
          />
          <View style={styles.clubInfo}>
            <Text style={styles.clubName}>{selectedBand?.name || 'ë™í˜¸íšŒ'}</Text>
            <Text style={styles.clubDescription}>{selectedBand?.description}</Text>
            <View style={styles.clubStats}>
              <Chip icon="account-group" style={styles.statChip}>
                {clubData.memberStats?.totalMembers || 0}ëª…
              </Chip>
              <Chip icon="calendar-month" style={styles.statChip}>
                ì´ë²ˆë‹¬ {clubData.memberStats?.thisMonthGames || 0}íšŒ
              </Chip>
            </View>
          </View>
        </View>
      </Card.Content>
    </Card>
  );

  const renderPaymentStatus = () => {
    if (!clubData.paymentStatus) return null;

    const { paid, amount, dueDate, method } = clubData.paymentStatus;

    return (
      <Card style={styles.paymentCard}>
        <Card.Content>
          <View style={styles.paymentHeader}>
            <Text style={styles.sectionTitle}>ğŸ’° ëª¨ì„ë¹„ í˜„í™©</Text>
            <Badge 
              style={[styles.badge, { backgroundColor: paid ? theme.colors.success : theme.colors.error }]}
            >
              {paid ? 'ë‚©ë¶€ì™„ë£Œ' : 'ë¯¸ë‚©'}
            </Badge>
          </View>
          <View style={styles.paymentDetails}>
            <Text style={styles.paymentAmount}>
              {amount.toLocaleString()}ì› ({method})
            </Text>
            <Text style={styles.paymentDue}>
              ë‚©ë¶€ ê¸°í•œ: {new Date(dueDate).toLocaleDateString('ko-KR')}
            </Text>
          </View>
          {!paid && (
            <Button 
              mode="contained" 
              style={styles.payButton}
              onPress={() => navigation.navigate('Payment')}
            >
              ëª¨ì„ë¹„ ë‚©ë¶€í•˜ê¸°
            </Button>
          )}
        </Card.Content>
      </Card>
    );
  };

  const renderUpcomingGames = () => (
    <Card style={styles.gamesCard}>
      <Card.Content>
        <View style={styles.sectionHeader}>
          <Text style={styles.sectionTitle}>ğŸ¸ ë‹¤ê°€ì˜¤ëŠ” ê²Œì„</Text>
          <Button 
            mode="text" 
            onPress={() => navigation.navigate('Games')}
          >
            ì „ì²´ë³´ê¸°
          </Button>
        </View>

        {clubData.upcomingGames.length > 0 ? (
          clubData.upcomingGames.map((game, index) => (
            <View key={game.id || index}>
              <List.Item
                title={game.title || 'ì •ê¸° ëª¨ì„'}
                description={`${new Date(game.gameDate).toLocaleDateString('ko-KR')} ${new Date(game.gameDate).toLocaleTimeString('ko-KR', { hour: '2-digit', minute: '2-digit' })}`}
                left={(props) => <List.Icon {...props} icon="badminton" />}
                right={(props) => (
                  <View style={styles.gameInfo}>
                    <Text style={styles.gameParticipants}>
                      {game.participants?.length || 0}/{game.maxParticipants || 8}ëª…
                    </Text>
                    <IconButton 
                      {...props} 
                      icon="chevron-right" 
                      onPress={() => navigation.navigate('GameDetail', { gameId: game.id })}
                    />
                  </View>
                )}
              />
              {index < clubData.upcomingGames.length - 1 && <Divider />}
            </View>
          ))
        ) : (
          <Text style={styles.emptyText}>ì˜ˆì •ëœ ê²Œì„ì´ ì—†ìŠµë‹ˆë‹¤</Text>
        )}

        <Button 
          mode="outlined" 
          style={styles.createGameButton}
          onPress={() => navigation.navigate('GameCreate', { clubId: selectedBand?.id })}
        >
          ìƒˆ ê²Œì„ ë§Œë“¤ê¸°
        </Button>
      </Card.Content>
    </Card>
  );

  const renderNotices = () => (
    <Card style={styles.noticesCard}>
      <Card.Content>
        <View style={styles.sectionHeader}>
          <Text style={styles.sectionTitle}>ğŸ“¢ ë™í˜¸íšŒ ê³µì§€</Text>
          <Button 
            mode="text" 
            onPress={() => navigation.navigate('BandNotices')}
          >
            ë°´ë“œì—ì„œ ë³´ê¸°
          </Button>
        </View>

        {clubData.recentNotices.length > 0 ? (
          clubData.recentNotices.slice(0, 3).map((notice, index) => (
            <View key={notice.post_key || index}>
              <List.Item
