        useNativeDriver: false,
      }).start();
    }
  }, [progress, animated]);

  const getSkillInfo = (level) => {
    const skillInfo = {
      beginner: {
        label: 'ì´ˆê¸‰',
        color: theme.colors.skillLevel.beginner,
        description: 'ê¸°ë³¸ê¸° ìŠµë“ ë‹¨ê³„',
        range: '800-1200',
      },
      intermediate: {
        label: 'ì¤‘ê¸‰',
        color: theme.colors.skillLevel.intermediate,
        description: 'ì•ˆì •ì ì¸ ë ë¦¬ ê°€ëŠ¥',
        range: '1200-1600',
      },
      advanced: {
        label: 'ê³ ê¸‰',
        color: theme.colors.skillLevel.advanced,
        description: 'ì „ìˆ ì  í”Œë ˆì´',
        range: '1600-1800',
      },
      expert: {
        label: 'ì „ë¬¸ê°€',
        color: theme.colors.skillLevel.expert,
        description: 'í´ëŸ½ ëŒ€í‘œê¸‰',
        range: '1800-2000',
      },
      pro: {
        label: 'í”„ë¡œ',
        color: theme.colors.skillLevel.pro,
        description: 'í”„ë¡œ ì„ ìˆ˜ê¸‰',
        range: '2000+',
      },
    };

    return skillInfo[level] || skillInfo.beginner;
  };

  const skillInfo = getSkillInfo(skillLevel);
  const radius = size / 2 - 10;
  const center = size / 2;

  const getProgressColor = (progress) => {
    if (progress < 0.3) return theme.colors.skillLevel.beginner;
    if (progress < 0.5) return theme.colors.skillLevel.intermediate;
    if (progress < 0.7) return theme.colors.skillLevel.advanced;
    if (progress < 0.9) return theme.colors.skillLevel.expert;
    return theme.colors.skillLevel.pro;
  };

  return (
    <Surface style={[styles.container, style]} elevation={2}>
      <View style={styles.meterContainer}>
        {/* ì›í˜• í”„ë¡œê·¸ë ˆìŠ¤ */}
        <View style={[styles.progressContainer, { width: size, height: size }]}>
          <Svg width={size} height={size}>
            {/* ë°°ê²½ ì› */}
            <Circle
              cx={center}
              cy={center}
              r={radius}
              stroke={theme.colors.surfaceVariant}
              strokeWidth="8"
              fill="none"
            />

            {/* í”„ë¡œê·¸ë ˆìŠ¤ ì› */}
            <Circle
              cx={center}
              cy={center}
              r={radius}
              stroke={getProgressColor(progress)}
              strokeWidth="8"
              fill="none"
              strokeDasharray={strokeDasharray}
              strokeDashoffset={strokeDashoffset}
              strokeLinecap="round"
              transform={`rotate(-90 ${center} ${center})`}
            />
          </Svg>

          {/* ì¤‘ì•™ ì •ë³´ */}
          <View style={styles.centerInfo}>
            <Text style={[styles.ratingText, { color: skillInfo.color }]}>
              {currentRating}
            </Text>
            <Text style={styles.ratingLabel}>ë ˆì´íŒ…</Text>
          </View>
        </View>

        {/* í”Œë ˆì´ì–´ ì •ë³´ */}
        {playerName && (
          <Text style={styles.playerName}>{playerName}</Text>
        )}

        {/* ìŠ¤í‚¬ ë ˆë²¨ ë°°ì§€ */}
        <Chip
          icon="trophy"
          style={[
            styles.skillChip,
            { backgroundColor: skillInfo.color }
          ]}
          textStyle={[
            styles.skillChipText,
            { color: theme.colors.surface }
          ]}
        >
          {skillInfo.label}
        </Chip>

        {/* ìƒì„¸ ì •ë³´ */}
        {showDetails && (
          <View style={styles.detailsContainer}>
            <Text style={styles.description}>{skillInfo.description}</Text>
            <Text style={styles.range}>ë ˆì´íŒ… ë²”ìœ„: {skillInfo.range}</Text>

            {/* ì§„í–‰ë¥  í‘œì‹œ */}
            <View style={styles.progressInfo}>
              <Text style={styles.progressLabel}>ì§„í–‰ë¥ </Text>
              <Text style={[styles.progressValue, { color: skillInfo.color }]}>
                {Math.round(progress * 100)}%
              </Text>
            </View>
          </View>
        )}
      </View>
    </Surface>
  );
};

const styles = StyleSheet.create({
  container: {
    borderRadius: theme.borderRadius.card,
