            textColor={theme.colors.primary}
          >
            ì „ì²´ë³´ê¸°
          </Button>
        </View>

        {clubData.upcomingGames.length > 0 ? (
          clubData.upcomingGames.map((game, index) => (
            <GradientCard
              key={game.id || index}
