// API ê¸°ë³¸ ì„¤ì •
const api = axios.create({
  baseURL: API_BASE_URL || 'http://localhost:3000/api/v1',
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// ìš”ì²­ ì¸í„°ì…‰í„° - ì¸ì¦ í† í° ìë™ ì¶”ê°€
api.interceptors.request.use(
  async (config) => {
    try {
      const token = await AsyncStorage.getItem('authToken');
      if (token) {
        config.headers.Authorization = `Bearer ${token}`;
      }
    } catch (error) {
      console.error('í† í° ì¡°íšŒ ì˜¤ë¥˜:', error);
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// ì‘ë‹µ ì¸í„°ì…‰í„° - ì—ëŸ¬ ì²˜ë¦¬ ë° í† í° ê°±ì‹ 
api.interceptors.response.use(
  (response) => {
    return response;
  },
  async (error) => {
    const originalRequest = error.config;

    // 401 Unauthorized - í† í° ë§Œë£Œ
    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      try {
        // ë¦¬í”„ë ˆì‹œ í† í°ìœ¼ë¡œ ìƒˆ ì•¡ì„¸ìŠ¤ í† í° íšë“
        const refreshToken = await AsyncStorage.getItem('refreshToken');
        if (refreshToken) {
          const response = await axios.post(`${API_BASE_URL}/auth/refresh`, {
            refreshToken
          });

          const { accessToken } = response.data.data;
          await AsyncStorage.setItem('authToken', accessToken);

          // ì›ë˜ ìš”ì²­ ì¬ì‹œë„
          originalRequest.headers.Authorization = `Bearer ${accessToken}`;
          return api(originalRequest);
        }
      } catch (refreshError) {
        // ë¦¬í”„ë ˆì‹œ ì‹¤íŒ¨ ì‹œ ë¡œê·¸ì•„ì›ƒ
        await AsyncStorage.multiRemove(['authToken', 'refreshToken', 'userInfo']);
        // ë¡œê·¸ì¸ í™”ë©´ìœ¼ë¡œ ë¦¬ë‹¤ì´ë ‰íŠ¸ëŠ” AuthContextì—ì„œ ì²˜ë¦¬
      }
    }

    return Promise.reject(error);
  }
);

// API ë©”ì„œë“œë“¤
export const authAPI = {
  // íšŒì›ê°€ì…
  signup: (userData) => api.post('/auth/signup', userData),

  // ë¡œê·¸ì¸
  login: (credentials) => api.post('/auth/login', credentials),

  // ë¡œê·¸ì•„ì›ƒ
  logout: () => api.post('/auth/logout'),

  // í† í° ê°±ì‹ 
  refreshToken: (refreshToken) => api.post('/auth/refresh', { refreshToken }),

  // ë¹„ë°€ë²ˆí˜¸ ì¬ì„¤ì • ìš”ì²­
  requestPasswordReset: (email) => api.post('/auth/forgot-password', { email }),

  // ë¹„ë°€ë²ˆí˜¸ ì¬ì„¤ì •
  resetPassword: (token, newPassword) => api.post('/auth/reset-password', { token, newPassword }),
