      const userBands = await this.getUserBands();
      const badmintonKeywords = ['ë°°ë“œë¯¼í„´', 'ë°°ë¯¼', 'ì…”í‹€ì½•', 'ë™ë°°ì¦', 'badminton'];

      return userBands.filter(band => {
