// ì†ë„ ì„¤ì • í•¨ìˆ˜ êµ¬í˜„
int CObj_Phy__RBT_CTRL::Set_Movement_Speed(MOVEMENT_SPEED speed)
{
    double actual_speed = 0;

    switch(speed) {
        case SPEED_NORMAL:
            actual_speed = 1000.0;  // 1000mm/s
            break;

        case SPEED_SLOW:  
            actual_speed = 300.0;   // 300mm/s
            break;

        case SPEED_ULTRA_SLOW:
            actual_speed = 100.0;   // 100mm/s  
            break;

        default:
            Log_Error("Invalid speed mode: %d", speed);
            return ERROR_INVALID_PARAMETER;
