    // ì‹¤ì œ ë¡œë´‡ì— ì†ë„ ëª…ë ¹ ì „ì†¡
    CString command;
    command.Format(_T("SET_SPEED %.1f"), actual_speed);

    int result = Send_Robot_Command(command);
    if(result == SUCCESS) {
        m_current_speed = speed;
        Log_Message("Robot speed set to %.1f mm/s", actual_speed);
    }

    return result;
}

