// ì „ì²´ ê°€ìŠ¤ ë¼ì¸ ì••ë ¥ ì ê²€
bool CObj__SAFETY_INTERLOCK::Check_All_Gas_Line_Pressures()
{
    Log_Message("Starting comprehensive gas line pressure check");

    bool all_passed = true;
    int failed_count = 0;

    for(int i = 0; i < m_gas_lines.size(); i++) {

        // ì‚¬ìš© ì¤‘ì¸ ë¼ì¸ë§Œ ì ê²€
        if(!m_gas_lines[i].is_active) {
            Log_Message("Skipping inactive gas line: %s", 
                       m_gas_lines[i].gas_name);
            continue;
        }

        bool line_ok = Check_Single_Gas_Line_Pressure(i);
        if(!line_ok) {
            all_passed = false;
            failed_count++;

            // ì¦‰ì‹œ ì•ŒëžŒ ë°œì†¡
            Send_Gas_Pressure_Alarm(i);
        }
    }

    if(all_passed) {
        Log_Message("All gas line pressures are within normal range");
    } else {
        Log_Error("Gas pressure check failed: %d/%d lines failed", 
                 failed_count, m_gas_lines.size());
    }

    return all_passed;
}

// ê°œë³„ ê°€ìŠ¤ ë¼ì¸ ì••ë ¥ ì ê²€
bool CObj__SAFETY_INTERLOCK::Check_Single_Gas_Line_Pressure(int line_id)
{
    if(line_id < 0 || line_id >= m_gas_lines.size()) {
        Log_Error("Invalid gas line ID: %d", line_id);
        return false;
    }

    GAS_LINE_INFO& line = m_gas_lines[line_id];
    double current_pressure = Get_Gas_Line_Pressure(line_id);

    // ì••ë ¥ ë²”ìœ„ í™•ì¸
    bool pressure_ok = (current_pressure >= line.min_pressure && 
                       current_pressure <= line.max_pressure);

    Log_Message("Gas line %s pressure check: %.1f psi (Range: %.1f-%.1f) - %s",
               line.gas_name,
               current_pressure,
               line.min_pressure,
               line.max_pressure,
               pressure_ok ? "PASS" : "FAIL");

    return pressure_ok;
}

// íŠ¹ì • ê°€ìŠ¤ ë¼ì¸ ì••ë ¥ ì¡°íšŒ
double CObj__SAFETY_INTERLOCK::Get_Gas_Line_Pressure(int line_id)
{
    if(line_id < 0 || line_id >= m_gas_lines.size()) {
        return -1.0;  // ì—ëŸ¬ í‘œì‹œ
    }

    // ì‹¤ì œë¡œëŠ” PLCë‚˜ ì••ë ¥ ì„¼ì„œë¡œë¶€í„° ë°ì´í„° ì½ê¸°
    // ì—¬ê¸°ì„œëŠ” ì‹œë®¬ë ˆì´ì…˜ ê°’ ì‚¬ìš©
    double simulated_pressure = 100.0 + (rand() % 20 - 10);  // 90~110 ë²”ìœ„

    return simulated_pressure;
}

// ê°€ìŠ¤ ì••ë ¥ ì•ŒëžŒ ë°œì†¡
void CObj__SAFETY_INTERLOCK::Send_Gas_Pressure_Alarm(int line_id)
{
    GAS_LINE_INFO& line = m_gas_lines[line_id];
    double current_pressure = Get_Gas_Line_Pressure(line_id);

    CString alarm_msg;
    alarm_msg.Format(_T("GAS_PRESSURE_ABNORMAL: %s line %.1f psi (Range: %.1f-%.1f)"),
                    line.gas_name,
                    current_pressure,
                    line.min_pressure,
                    line.max_pressure);

    // ì•ŒëžŒ ì‹œìŠ¤í…œì— ì „ì†¡
    Send_System_Alarm("GAS_PRESSURE", alarm_msg);

    // ê¸´ê¸‰í•œ ê²½ìš° ê°€ìŠ¤ ê³µê¸‰ ìžë™ ì°¨ë‹¨
    if(current_pressure > line.max_pressure * 1.5) {  // 150% ì´ˆê³¼ì‹œ
        Log_Message("Emergency gas shutoff for %s line", line.gas_name);
        Emergency_Close_Gas_Valve(line_id);
    }
}
