    Log_Message("Total data collection time: %.3f ms", total_time * 1000);

    if(total_time > 0.1) {  // 100ms ì´ìƒì´ë©´ ì„±ëŠ¥ ë¬¸ì œ
        Log_Error("Data collection too slow: %.3f ms", total_time * 1000);
