### Step 2: ë©€í‹°ìŠ¤ë ˆë”©ìœ¼ë¡œ ì„±ëŠ¥ ê°œì„ 
```cpp
// ë©€í‹°ìŠ¤ë ˆë“œ ë°ì´í„° ìˆ˜ì§‘ êµ¬í˜„

#include <thread>
#include <mutex>
#include <vector>

class CHighPerformanceDataCollector
{
private:
    vector<thread> m_worker_threads;
    vector<double> m_sensor_data;
    mutex m_data_mutex;
    bool m_stop_collection;

public:
    int Start_Concurrent_Data_Collection()
    {
        m_stop_collection = false;

        // ì„¼ì„œë¥¼ ì—¬ëŸ¬ ê·¸ë£¹ìœ¼ë¡œ ë‚˜ëˆ„ì–´ ë³‘ë ¬ ì²˜ë¦¬
        int sensors_per_thread = sensor_count / 4;  // 4ê°œ ìŠ¤ë ˆë“œ ì‚¬ìš©

        for(int thread_id = 0; thread_id < 4; thread_id++) {

            int start_sensor = thread_id * sensors_per_thread;
            int end_sensor = (thread_id == 3) ? sensor_count : (thread_id + 1) * sensors_per_thread;

            m_worker_threads.emplace_back([this, thread_id, start_sensor, end_sensor]() {
                Worker_Thread_Function(thread_id, start_sensor, end_sensor);
            });
        }

        Log_Message("Started %d data collection threads", m_worker_threads.size());
        return SUCCESS;
    }

    void Worker_Thread_Function(int thread_id, int start_sensor, int end_sensor)
    {
        Log_Message("Data collection thread %d started (sensors %d-%d)", 
                   thread_id, start_sensor, end_sensor - 1);

        while(!m_stop_collection) {

            // ê° ìŠ¤ë ˆë“œê°€ ë‹´ë‹¹í•˜ëŠ” ì„¼ì„œë“¤ì˜ ë°ì´í„° ìˆ˜ì§‘
            for(int i = start_sensor; i < end_sensor; i++) {
                double value = Read_Sensor_Value(i);

                // ë®¤í…ìŠ¤ë¡œ ë°ì´í„° ë³´í˜¸
                {
                    lock_guard<mutex> lock(m_data_mutex);
                    m_sensor_data[i] = value;
                }
            }

            // 100msë§ˆë‹¤ ìˆ˜ì§‘
            this_thread::sleep_for(chrono::milliseconds(100));
        }

        Log_Message("Data collection thread %d stopped", thread_id);
    }

    vector<double> Get_Latest_Sensor_Data()
    {
        lock_guard<mutex> lock(m_data_mutex);
        return m_sensor_data;  // ë³µì‚¬ë³¸ ë°˜í™˜
    }

    void Stop_Data_Collection()
    {
        m_stop_collection = true;

        // ëª¨ë“  ìŠ¤ë ˆë“œ ì¢…ë£Œ ëŒ€ê¸°
        for(auto& thread : m_worker_threads) {
            if(thread.joinable()) {
                thread.join();
            }
        }

        m_worker_threads.clear();
        Log_Message("All data collection threads stopped");
    }
