### Step 4: ë¡œê·¸ ë©”ì‹œì§€ ì¶”ê°€í•˜ê¸°
```cpp
// ìƒì„¸í•œ ë¡œê·¸ ë©”ì‹œì§€ ì¶”ê°€ ì˜ˆì‹œ

int CObj_Phy__RBT_CTRL::Move_To_Position(double x, double y, double z)
{
    // ì‹œìž‘ ë¡œê·¸
    TRACE(_T("[TMC] Move_To_Position called: (%.3f, %.3f, %.3f)\n"), x, y, z);
    Log_Message("Robot move request: Target(%.3f, %.3f, %.3f)", x, y, z);

    // í˜„ìž¬ ìœ„ì¹˜ ê¸°ë¡
    ROBOT_POSITION current = Get_Current_Position();
    Log_Message("Current position: (%.3f, %.3f, %.3f)", 
               current.x, current.y, current.z);

    // ì´ë™ ê±°ë¦¬ ê³„ì‚° ë° ê¸°ë¡
    double distance = sqrt(pow(x - current.x, 2) + 
                          pow(y - current.y, 2) + 
                          pow(z - current.z, 2));
    Log_Message("Move distance: %.3f mm", distance);

    // íŒŒë¼ë¯¸í„° ê²€ì¦ ìƒì„¸í™”
    bool param_valid = true;
    if(x < MIN_X_POSITION || x > MAX_X_POSITION) {
        Log_Error("X position out of range: %.3f (Range: %.1f ~ %.1f)", 
                 x, MIN_X_POSITION, MAX_X_POSITION);
        param_valid = false;
    }

    if(y < MIN_Y_POSITION || y > MAX_Y_POSITION) {
        Log_Error("Y position out of range: %.3f (Range: %.1f ~ %.1f)", 
                 y, MIN_Y_POSITION, MAX_Y_POSITION);
        param_valid = false;
    }

    if(z < MIN_Z_POSITION || z > MAX_Z_POSITION) {
        Log_Error("Z position out of range: %.3f (Range: %.1f ~ %.1f)", 
                 z, MIN_Z_POSITION, MAX_Z_POSITION);
        param_valid = false;
    }

    if(!param_valid) {
        return ERROR_INVALID_POSITION;
    }

    // ëª…ë ¹ ì „ì†¡
    CString command;
    command.Format(_T("MOVE %.3f %.3f %.3f"), x, y, z);
    Log_Message("Sending robot command: %s", command);

    DWORD start_time = GetTickCount();
    int result = Send_Robot_Command(command);
    DWORD elapsed_time = GetTickCount() - start_time;

    if(result == SUCCESS) {
        Log_Message("Robot move completed successfully in %d ms", elapsed_time);

        // ìµœì¢… ìœ„ì¹˜ í™•ì¸
        ROBOT_POSITION final = Get_Current_Position();
        double position_error = sqrt(pow(x - final.x, 2) + 
                                   pow(y - final.y, 2) + 
                                   pow(z - final.z, 2));

        Log_Message("Final position: (%.3f, %.3f, %.3f), Error: %.3f mm", 
                   final.x, final.y, final.z, position_error);

        if(position_error > POSITION_TOLERANCE) {
