template<typename T>
class CMemoryPool
{
private:
    vector<T> m_pool;
    queue<T*> m_available;
    mutex m_pool_mutex;

public:
    CMemoryPool(int initial_size = 100)
    {
        m_pool.reserve(initial_size);

        for(int i = 0; i < initial_size; i++) {
            m_pool.emplace_back();
            m_available.push(&m_pool[i]);
        }

        Log_Message("Memory pool initialized with %d objects", initial_size);
    }

    T* Acquire()
    {
        lock_guard<mutex> lock(m_pool_mutex);

        if(m_available.empty()) {
            // í’€ì´ ë¹„ì–´ìžˆìœ¼ë©´ í™•ìž¥
            int old_size = m_pool.size();
            m_pool.resize(old_size * 2);

            for(int i = old_size; i < m_pool.size(); i++) {
                m_available.push(&m_pool[i]);
            }

            Log_Message("Memory pool expanded to %d objects", m_pool.size());
        }

        T* obj = m_available.front();
        m_available.pop();

        return obj;
    }

    void Release(T* obj)
    {
        if(obj) {
            // ê°ì²´ ì´ˆê¸°í™”
            *obj = T{};

            lock_guard<mutex> lock(m_pool_mutex);
            m_available.push(obj);
