class CRobotCalibration
{
public:
    int Perform_Auto_Calibration()
    {
        Log_Message("Starting robot auto-calibration");

        // 1. í™ˆ ìœ„ì¹˜ë¡œ ì´ë™
        if(Move_To_Home() != SUCCESS) {
            return ERROR_HOME_MOVE_FAILED;
        }

        // 2. ìº˜ë¦¬ë¸Œë ˆì´ì…˜ í¬ì¸íŠ¸ë“¤ í™•ì¸
        std::vector<CalibrationPoint> calib_points = {
            {100, 100, 50},   // ì¢Œìƒë‹¨
            {400, 100, 50},   // ìš°ìƒë‹¨  
            {400, 300, 50},   // ìš°í•˜ë‹¨
            {100, 300, 50},   // ì¢Œí•˜ë‹¨
            {250, 200, 50}    // ì¤‘ì•™
        };

        for(size_t i = 0; i < calib_points.size(); i++) {

            // ëª…ë ¹ ìœ„ì¹˜ë¡œ ì´ë™
            RobotPosition command_pos = calib_points[i].command_pos;
            int result = Move_To_Position(command_pos.x, command_pos.y, command_pos.z);

            if(result != SUCCESS) {
                Log_Error("Failed to move to calibration point %zu", i);
                return ERROR_CALIBRATION_MOVE_FAILED;
            }

            // ì‹¤ì œ ìœ„ì¹˜ ì¸¡ì • (ë¹„ì „ ì‹œìŠ¤í…œ ë˜ëŠ” ì„¼ì„œ ì‚¬ìš©)
            RobotPosition actual_pos = Measure_Actual_Position();

            // ì˜¤ì°¨ ê³„ì‚°
            PositionError error;
            error.x = actual_pos.x - command_pos.x;
            error.y = actual_pos.y - command_pos.y;
            error.z = actual_pos.z - command_pos.z;

            Log_Message("Point %zu - Command:(%.2f,%.2f,%.2f) Actual:(%.2f,%.2f,%.2f) Error:(%.3f,%.3f,%.3f)",
                       i, command_pos.x, command_pos.y, command_pos.z,
                       actual_pos.x, actual_pos.y, actual_pos.z,
                       error.x, error.y, error.z);

            // ì˜¤ì°¨ê°€ í—ˆìš© ë²”ìœ„ë¥¼ ì´ˆê³¼í•˜ë©´ ë³´ì • í…Œì´ë¸” ì—…ë°ì´íŠ¸
            if(abs(error.x) > POSITION_TOLERANCE || 
               abs(error.y) > POSITION_TOLERANCE || 
               abs(error.z) > POSITION_TOLERANCE) {

                Update_Calibration_Table(command_pos, error);
            }
        }

        // 3. ë³´ì • í…Œì´ë¸” ì €ìž¥
        Save_Calibration_Table();

        // 4. ë³´ì • í›„ ìž¬ê²€ì¦
        return Verify_Calibration_Accuracy();
    }

private:
    void Update_Calibration_Table(RobotPosition command, PositionError error)
    {
        // ì£¼ë³€ ê²©ìžì ë“¤ì— ëŒ€í•œ ë³´ê°„ì„ í†µí•œ ë³´ì •ê°’ ê³„ì‚°
        int grid_x = (int)(command.x / GRID_SIZE);
        int grid_y = (int)(command.y / GRID_SIZE);

        m_calibration_table[grid_x][grid_y].x_error = error.x;
        m_calibration_table[grid_x][grid_y].y_error = error.y;
        m_calibration_table[grid_x][grid_y].z_error = error.z;

