public:
    int Pick_Wafer_With_Retry(int cassette_id, int slot_number)
    {
        const int MAX_RETRY = 3;

        for(int retry = 0; retry < MAX_RETRY; retry++) {

            Log_Message("Wafer pick attempt %d/%d (Cassette:%d, Slot:%d)", 
                       retry + 1, MAX_RETRY, cassette_id, slot_number);

            // 1. ì‚¬ì „ ì¡°ê±´ í™•ì¸
            if(!Pre_Pick_Validation(cassette_id, slot_number)) {
