                Log_Warning("Wafer not detected on blade after pick");
                Release_Vacuum();  // ì•ˆì „ì„ ìœ„í•´ ì§„ê³µ í•´ì œ
            }

            // ìž¬ì‹œë„ ì „ ëŒ€ê¸°
            if(retry < MAX_RETRY - 1) {
                Sleep(500);  // 500ms ëŒ€ê¸°
            }
        }

        Log_Error("Wafer pick failed after %d attempts", MAX_RETRY);
        Send_Alarm("WAFER_PICK_FAILED", cassette_id, slot_number);
        return ERROR_WAFER_PICK_FAILED;
    }

private:
    bool Pre_Pick_Validation(int cassette_id, int slot_number)
    {
        // ì¹´ì„¸íŠ¸ ì¡´ìž¬ í™•ì¸
        if(!Is_Cassette_Present(cassette_id)) {
            Log_Error("Cassette %d not present", cassette_id);
