    int Execute_Gradual_Pick(int cassette_id, int slot_number)
    {
        // 1. ìŠ¬ë¡¯ ìœ„ì¹˜ë³´ë‹¤ ì•½ê°„ ë†’ì€ ê³³ì—ì„œ ëŒ€ê¸°
        RobotPosition approach_pos = Get_Slot_Position(cassette_id, slot_number);
        approach_pos.z += APPROACH_HEIGHT;  // 5mm ìœ„

        if(Move_To_Position_Slow(approach_pos) != SUCCESS) {
            return ERROR_APPROACH_FAILED;
        }

        // 2. ì²œì²œížˆ í•˜ê°•í•˜ë©´ì„œ ì›¨ì´í¼ ê°ì§€
        for(double z = approach_pos.z; z >= approach_pos.z - PICK_DEPTH; z -= 0.1) {

            Move_Z_Axis(z);
