public:
    int Monitor_Process_Stability(const std::string& recipe_name)
    {
        Log_Message("Starting process stability monitoring for %s", recipe_name.c_str());

        ProcessDataPoint current_data = Collect_Current_Process_Data();
        m_history_data.push_back(current_data);

        // ìµœëŒ€ ë³´ê´€ ê°œìˆ˜ ì´ˆê³¼ì‹œ ì˜¤ëž˜ëœ ë°ì´í„° ì‚­ì œ
        if(m_history_data.size() > MAX_HISTORY) {
