        }

        // ê° íŒŒë¼ë¯¸í„°ë³„ ì•ˆì •ì„± ë¶„ì„
        StabilityAnalysis temp_analysis = Analyze_Parameter_Stability("Temperature");
        StabilityAnalysis pressure_analysis = Analyze_Parameter_Stability("Pressure");
        StabilityAnalysis power_analysis = Analyze_Parameter_Stability("RF_Power");

        // ë¶ˆì•ˆì •í•œ íŒŒë¼ë¯¸í„° ê°ì§€ì‹œ ê²½ê³ 
        if(temp_analysis.coefficient_of_variation > 0.02) {  // 2% ì´ìƒ ë³€ë™
            Log_Warning("Temperature instability detected: CV=%.3f%%", 
