// ë°ì´í„°ë² ì´ìŠ¤ ì„±ëŠ¥ ìµœì í™”
class COptimizedDatabase
{
private:
    std::map<std::string, PreparedStatement> m_prepared_statements;
    ConnectionPool m_connection_pool;

public:
    // ì—°ê²° í’€ì„ í†µí•œ ì„±ëŠ¥ í–¥ìƒ
    class ConnectionPool
    {
    private:
        std::queue<std::shared_ptr<DatabaseConnection>> m_connections;
        std::mutex m_pool_mutex;
        const int POOL_SIZE = 5;

    public:
        void Initialize()
        {
            for(int i = 0; i < POOL_SIZE; i++) {
                auto conn = std::make_shared<DatabaseConnection>();
                conn->Connect("server=localhost;database=MINESOTA;");
                m_connections.push(conn);
            }

            Log_Message("Database connection pool initialized with %d connections", POOL_SIZE);
        }

        std::shared_ptr<DatabaseConnection> AcquireConnection()
        {
            std::lock_guard<std::mutex> lock(m_pool_mutex);

            if(m_connections.empty()) {
                // í’€ì´ ë¹„ì–´ìžˆìœ¼ë©´ ìƒˆ ì—°ê²° ìƒì„±
                auto conn = std::make_shared<DatabaseConnection>();
                conn->Connect("server=localhost;database=MINESOTA;");
                return conn;
            }

            auto conn = m_connections.front();
            m_connections.pop();
            return conn;
        }

        void ReleaseConnection(std::shared_ptr<DatabaseConnection> conn)
        {
            std::lock_guard<std::mutex> lock(m_pool_mutex);

            if(conn->IsValid()) {
                m_connections.push(conn);
            }
        }
    };

    // ì¤€ë¹„ëœ ëª…ë ¹ë¬¸ì„ í†µí•œ ì„±ëŠ¥ í–¥ìƒ
    int Execute_Optimized_Query(const std::string& query_name, 
                               const std::vector<QueryParameter>& params)
    {
        // ì¤€ë¹„ëœ ëª…ë ¹ë¬¸ ìºì‹œì—ì„œ ì¡°íšŒ
        auto it = m_prepared_statements.find(query_name);
        if(it == m_prepared_statements.end()) {

            // ìƒˆë¡œìš´ ì¤€ë¹„ëœ ëª…ë ¹ë¬¸ ìƒì„±
            PreparedStatement stmt;
            if(query_name == "INSERT_WAFER_LOG") {
                stmt.query = "INSERT INTO WaferLog (WaferID, Timestamp, Operation, Result) VALUES (?, ?, ?, ?)";
            } else if(query_name == "SELECT_PROCESS_HISTORY") {
                stmt.query = "SELECT * FROM ProcessHistory WHERE WaferID = ? AND Timestamp > ?";
            }

            auto conn = m_connection_pool.AcquireConnection();
            stmt.prepared = conn->PrepareStatement(stmt.query);
            m_prepared_statements[query_name] = stmt;

            Log_Message("Prepared statement created for: %s", query_name.c_str());
        }

        // íŒŒë¼ë¯¸í„° ë°”ì¸ë”© ë° ì‹¤í–‰
        PreparedStatement& stmt = m_prepared_statements[query_name];
        for(size_t i = 0; i < params.size(); i++) {
            stmt.prepared->BindParameter(i + 1, params[i]);
        }

        auto start_time = std::chrono::high_resolution_clock::now();
        int result = stmt.prepared->Execute();
        auto end_time = std::chrono::high_resolution_clock::now();

        auto duration = std::chrono::duration_cast<std::chrono::microseconds>(end_time - start_time);

        Log_Trace("Query executed: %s (%.3f ms)", 
                 query_name.c_str(), duration.count() / 1000.0);

        return result;
    }

    // ë°°ì¹˜ ì²˜ë¦¬ë¥¼ í†µí•œ ëŒ€ëŸ‰ ë°ì´í„° ì‚½ìž… ìµœì í™”
    int Insert_Batch_Data(const std::vector<WaferLogData>& data_list)
    {
        const int BATCH_SIZE = 100;

        auto conn = m_connection_pool.AcquireConnection();
        conn->BeginTransaction();

        try {
            for(size_t i = 0; i < data_list.size(); i += BATCH_SIZE) {

                std::stringstream batch_query;
                batch_query << "INSERT INTO WaferLog (WaferID, Timestamp, Operation, Result) VALUES ";

                size_t batch_end = std::min(i + BATCH_SIZE, data_list.size());
                for(size_t j = i; j < batch_end; j++) {

                    if(j > i) batch_query << ", ";

                    batch_query << "('" << data_list[j].wafer_id << "', "
                               << "'" << data_list[j].timestamp << "', "
                               << "'" << data_list[j].operation << "', "
                               << "'" << data_list[j].result << "')";
                }

                conn->ExecuteQuery(batch_query.str());

                Log_Trace("Batch inserted: %zu records", batch_end - i);
            }

            conn->CommitTransaction();
            m_connection_pool.ReleaseConnection(conn);

            Log_Message("Batch insert completed: %zu total records", data_list.size());
            return SUCCESS;

        } catch(const std::exception& e) {
            conn->RollbackTransaction();
            m_connection_pool.ReleaseConnection(conn);

            Log_Error("Batch insert failed: %s", e.what());
            return ERROR_DATABASE_BATCH_FAILED;
        }
    }
};
