// ì•ˆì •ì ì¸ TCP í†µì‹  êµ¬í˜„
class CStableTcpClient
{
private:
    SOCKET m_socket;
    bool m_connected;
    std::thread m_reconnect_thread;
    std::mutex m_socket_mutex;

public:
    int Connect_With_Retry(const char* ip, int port, int max_retry = 3)
    {
        for(int i = 0; i < max_retry; i++) {

            Log_Message("Connection attempt %d/%d to %s:%d", i+1, max_retry, ip, port);

            m_socket = socket(AF_INET, SOCK_STREAM, 0);
            if(m_socket == INVALID_SOCKET) {
                Log_Error("Socket creation failed: %d", WSAGetLastError());
                continue;
            }

            // ì†Œì¼“ ì˜µì…˜ ì„¤ì •
            int timeout = 5000;  // 5ì´ˆ íƒ€ìž„ì•„ì›ƒ
            setsockopt(m_socket, SOL_SOCKET, SO_RCVTIMEO, 
                      (char*)&timeout, sizeof(timeout));
            setsockopt(m_socket, SOL_SOCKET, SO_SNDTIMEO, 
                      (char*)&timeout, sizeof(timeout));

            // Keep-alive ì„¤ì •
            BOOL keep_alive = TRUE;
            setsockopt(m_socket, SOL_SOCKET, SO_KEEPALIVE, 
                      (char*)&keep_alive, sizeof(keep_alive));

            // ì—°ê²° ì‹œë„
            sockaddr_in server_addr;
            server_addr.sin_family = AF_INET;
            server_addr.sin_port = htons(port);
            inet_pton(AF_INET, ip, &server_addr.sin_addr);

            if(connect(m_socket, (sockaddr*)&server_addr, sizeof(server_addr)) == 0) {
                m_connected = true;
                Log_Message("Connected successfully to %s:%d", ip, port);

                // ì—°ê²° ìƒíƒœ ëª¨ë‹ˆí„°ë§ ìŠ¤ë ˆë“œ ì‹œìž‘
                Start_Connection_Monitor();
                return SUCCESS;
            }

            Log_Error("Connection attempt %d failed: %d", i+1, WSAGetLastError());
            closesocket(m_socket);
            Sleep(2000);  // 2ì´ˆ ëŒ€ê¸° í›„ ìž¬ì‹œë„
        }

        return ERROR_CONNECTION_FAILED;
    }

    int Send_With_Confirmation(const char* data, int length)
    {
        std::lock_guard<std::mutex> lock(m_socket_mutex);

        if(!m_connected) {
            return ERROR_NOT_CONNECTED;
        }

        // ë°ì´í„° ì „ì†¡
        int sent = send(m_socket, data, length, 0);
        if(sent == SOCKET_ERROR) {
            Log_Error("Send failed: %d", WSAGetLastError());
            m_connected = false;
            return ERROR_SEND_FAILED;
        }

        // ì‘ë‹µ í™•ì¸ (í•„ìš”ì‹œ)
        char ack_buffer[10];
        int received = recv(m_socket, ack_buffer, sizeof(ack_buffer), 0);
        if(received <= 0) {
            Log_Warning("No acknowledgment received");
            return ERROR_NO_RESPONSE;
        }

        return SUCCESS;
    }

private:
    void Start_Connection_Monitor()
    {
        m_reconnect_thread = std::thread([this]() {

            while(m_connected) {

                // 1ì´ˆë§ˆë‹¤ ì—°ê²° ìƒíƒœ í™•ì¸
                Sleep(1000);

                // Keep-alive ì²´í¬
                char test_data = 0;
                int result = send(m_socket, &test_data, 0, 0);
                if(result == SOCKET_ERROR) {

                    int error = WSAGetLastError();
                    if(error != WSAEWOULDBLOCK) {
                        Log_Warning("Connection lost, attempting reconnect...");
                        m_connected = false;

                        // ìžë™ ìž¬ì—°ê²° ì‹œë„
                        // Reconnect_Automatically();
                    }
                }
            }
        });

        m_reconnect_thread.detach();
    }
};
