    AmsAddr m_plc_addr;
    std::map<std::string, PLCVariable> m_variable_cache;
    std::mutex m_comm_mutex;

public:
    int Read_PLC_Variable_Safe(const std::string& var_name, void* data, int size)
    {
        std::lock_guard<std::mutex> lock(m_comm_mutex);

        const int MAX_RETRY = 3;
        const int RETRY_DELAY = 100;  // 100ms

        for(int retry = 0; retry < MAX_RETRY; retry++) {

            long result = AdsSyncReadReq(&m_plc_addr, ADSIGRP_SYM_VALBYHND, 
                                        GetVariableHandle(var_name), 
                                        size, data);

            if(result == 0) {
                // ë°ì´í„° ìœ íš¨ì„± ê²€ì‚¬
                if(Validate_PLC_Data(var_name, data, size)) {

                    // ìºì‹œ ì—…ë°ì´íŠ¸
                    Update_Variable_Cache(var_name, data, size);

                    Log_Trace("PLC read successful: %s (attempt %d)", 
                             var_name.c_str(), retry + 1);
                    return SUCCESS;
                }

                Log_Warning("PLC data validation failed: %s", var_name.c_str());
            }

            Log_Warning("PLC read failed: %s, error: 0x%08X (attempt %d)", 
                       var_name.c_str(), result, retry + 1);

            if(retry < MAX_RETRY - 1) {
                Sleep(RETRY_DELAY);
