        if(var_name.find("Pressure") != std::string::npos) {
            double pressure = *(double*)data;
            return (pressure >= 0.0 && pressure <= 1000.0);  // ì••ë ¥ ë²”ìœ„ ì²´í¬
        }

        return true;  // ê¸°ë³¸ì ìœ¼ë¡œ ìœ íš¨í•˜ë‹¤ê³  ê°€ì •
    }
};
