class CRobotCalibration
{
public:
    int Perform_Auto_Calibration()
    {
        Log_Message("Starting robot auto-calibration");

        // 1. í™ˆ ìœ„ì¹˜ë¡œ ì´ë™
        if(Move_To_Home() != SUCCESS) {
            return ERROR_HOME_MOVE_FAILED;
        }

        // 2. ìº˜ë¦¬ë¸Œë ˆì´ì…˜ í¬ì¸íŠ¸ë“¤ í™•ì¸
        std::vector<CalibrationPoint> calib_points = {
            {100, 100, 50},   // ì¢Œìƒë‹¨
            {400, 100, 50},   // ìš°ìƒë‹¨  
            {400, 300, 50},   // ìš°í•˜ë‹¨
            {100, 300, 50},   // ì¢Œí•˜ë‹¨
            {250, 200, 50}    // ì¤‘ì•™
        };

        for(size_t i = 0; i < calib_points.size(); i++) {

            // ëª…ë ¹ ìœ„ì¹˜ë¡œ ì´ë™
            RobotPosition command_pos = calib_points[i].command_pos;
            int result = Move_To_Position(command_pos.x, command_pos.y, command_pos.z);

            if(result != SUCCESS) {
                Log_Error("Failed to move to calibration point %zu", i);
                return ERROR_CALIBRATION_MOVE_FAILED;
