            // ì‹¤ì œ ìœ„ì¹˜ ì¸¡ì • (ë¹„ì „ ì‹œìŠ¤í…œ ë˜ëŠ” ì„¼ì„œ ì‚¬ìš©)
            RobotPosition actual_pos = Measure_Actual_Position();

            // ì˜¤ì°¨ ê³„ì‚°
            PositionError error;
            error.x = actual_pos.x - command_pos.x;
            error.y = actual_pos.y - command_pos.y;
            error.z = actual_pos.z - command_pos.z;

            Log_Message("Point %zu - Command:(%.2f,%.2f,%.2f) Actual:(%.2f,%.2f,%.2f) Error:(%.3f,%.3f,%.3f)",
                       i, command_pos.x, command_pos.y, command_pos.z,
                       actual_pos.x, actual_pos.y, actual_pos.z,
                       error.x, error.y, error.z);

            // ì˜¤ì°¨ê°€ í—ˆìš© ë²”ìœ„ë¥¼ ì´ˆê³¼í•˜ë©´ ë³´ì • í…Œì´ë¸” ì—…ë°ì´íŠ¸
            if(abs(error.x) > POSITION_TOLERANCE || 
               abs(error.y) > POSITION_TOLERANCE || 
               abs(error.z) > POSITION_TOLERANCE) {

                Update_Calibration_Table(command_pos, error);
            }
        }

        // 3. ë³´ì • í…Œì´ë¸” ì €ìž¥
        Save_Calibration_Table();

        // 4. ë³´ì • í›„ ìž¬ê²€ì¦
        return Verify_Calibration_Accuracy();
    }

