        // ë¶ˆì•ˆì •í•œ íŒŒë¼ë¯¸í„° ê°ì§€ì‹œ ê²½ê³ 
        if(temp_analysis.coefficient_of_variation > 0.02) {  // 2% ì´ìƒ ë³€ë™
            Log_Warning("Temperature instability detected: CV=%.3f%%", 
                       temp_analysis.coefficient_of_variation * 100);
            Send_Process_Warning("TEMPERATURE_UNSTABLE", temp_analysis.current_value);
        }

        if(pressure_analysis.coefficient_of_variation > 0.05) {  // 5% ì´ìƒ ë³€ë™
            Log_Warning("Pressure instability detected: CV=%.3f%%", 
                       pressure_analysis.coefficient_of_variation * 100);
            Send_Process_Warning("PRESSURE_UNSTABLE", pressure_analysis.current_value);
        }

        if(power_analysis.coefficient_of_variation > 0.03) {  // 3% ì´ìƒ ë³€ë™
            Log_Warning("RF Power instability detected: CV=%.3f%%", 
                       power_analysis.coefficient_of_variation * 100);
            Send_Process_Warning("POWER_UNSTABLE", power_analysis.current_value);
        }

        // íŠ¸ë Œë“œ ë¶„ì„ (ìƒìŠ¹/í•˜ê°• ì¶”ì„¸ ê°ì§€)
        TrendAnalysis trend = Analyze_Process_Trend();
        if(trend.is_drifting) {
            Log_Warning("Process drift detected in %s: trend=%.4f/hour", 
                       trend.parameter_name.c_str(), trend.drift_rate);
            Suggest_Corrective_Actions(trend);
        }

        return SUCCESS;
    }

private:
    StabilityAnalysis Analyze_Parameter_Stability(const std::string& param_name)
    {
        StabilityAnalysis analysis;
        std::vector<double> values;

        // íŒŒë¼ë¯¸í„°ë³„ ê°’ ì¶”ì¶œ
        for(const auto& data : m_history_data) {
            if(param_name == "Temperature") {
                values.push_back(data.temperature);
            } else if(param_name == "Pressure") {
                values.push_back(data.pressure);
            } else if(param_name == "RF_Power") {
                values.push_back(data.rf_power);
            }
        }

        // í†µê³„ ê³„ì‚°
        analysis.mean = Calculate_Mean(values);
        analysis.std_dev = Calculate_StdDev(values, analysis.mean);
        analysis.coefficient_of_variation = analysis.std_dev / analysis.mean;
        analysis.current_value = values.back();

        return analysis;
    }

    void Suggest_Corrective_Actions(const TrendAnalysis& trend)
    {
        Log_Message("=== CORRECTIVE ACTION SUGGESTIONS ===");

        if(trend.parameter_name == "Temperature") {
