// ë¬¸ì œ ì¶”ì  ì‹œìŠ¤í…œ êµ¬í˜„
class CIssueTracker
{
private:
    struct Issue {
        int issue_id;
        std::string title;
        std::string description;
        IssueSeverity severity;
        IssueStatus status;
        std::string assigned_to;
        std::time_t created_time;
        std::time_t updated_time;
        std::vector<IssueComment> comments;
    };

    std::map<int, Issue> m_issues;
    int m_next_issue_id;

public:
    int Create_Issue(const std::string& title, const std::string& description, 
                    IssueSeverity severity, const std::string& reporter)
    {
        Issue new_issue;
        new_issue.issue_id = m_next_issue_id++;
        new_issue.title = title;
        new_issue.description = description;
        new_issue.severity = severity;
        new_issue.status = STATUS_OPEN;
        new_issue.created_time = std::time(nullptr);
        new_issue.updated_time = new_issue.created_time;

        // ì‹¬ê°ë„ì— ë”°ë¥¸ ìžë™ í• ë‹¹
        switch(severity) {
            case CRITICAL:
                new_issue.assigned_to = "senior_engineer@company.com";
                Send_Immediate_Alert(new_issue);
                break;

            case HIGH:
                new_issue.assigned_to = "team_lead@company.com";
                Send_Priority_Alert(new_issue);
                break;

            case MEDIUM:
                new_issue.assigned_to = "engineer@company.com";
                break;

            case LOW:
                new_issue.assigned_to = "junior_engineer@company.com";
                break;
        }

        m_issues[new_issue.issue_id] = new_issue;

        Log_Message("Issue created: #%d - %s (Severity: %s, Assigned: %s)",
                   new_issue.issue_id, title.c_str(), 
                   SeverityToString(severity).c_str(),
                   new_issue.assigned_to.c_str());

        return new_issue.issue_id;
    }

    void Update_Issue_Status(int issue_id, IssueStatus new_status, 
                           const std::string& comment = "")
    {
        auto it = m_issues.find(issue_id);
        if(it == m_issues.end()) {
            Log_Error("Issue #%d not found", issue_id);
            return;
        }

        Issue& issue = it->second;
        IssueStatus old_status = issue.status;
        issue.status = new_status;
        issue.updated_time = std::time(nullptr);

        if(!comment.empty()) {
            IssueComment new_comment;
            new_comment.author = "system";
            new_comment.content = comment;
            new_comment.timestamp = issue.updated_time;
            issue.comments.push_back(new_comment);
        }

        Log_Message("Issue #%d status changed: %s â†’ %s", 
                   issue_id, StatusToString(old_status).c_str(),
                   StatusToString(new_status).c_str());

        // ìƒíƒœ ë³€ê²½ ì•Œë¦¼
        if(new_status == STATUS_RESOLVED) {
            Send_Resolution_Notification(issue);
        } else if(new_status == STATUS_REOPENED) {
            Send_Reopen_Alert(issue);
        }
    }

    std::vector<Issue> Get_Open_Issues_By_Severity(IssueSeverity min_severity)
    {
        std::vector<Issue> result;

        for(const auto& pair : m_issues) {
            const Issue& issue = pair.second;

            if(issue.status != STATUS_CLOSED && 
               issue.status != STATUS_RESOLVED &&
               issue.severity >= min_severity) {

                result.push_back(issue);
            }
        }

        // ì‹¬ê°ë„ ë° ìƒì„±ì‹œê°„ ìˆœìœ¼ë¡œ ì •ë ¬
        std::sort(result.begin(), result.end(), [](const Issue& a, const Issue& b) {
            if(a.severity != b.severity) {
                return a.severity > b.severity;  // ì‹¬ê°ë„ ë†’ì€ ìˆœ
            }
            return a.created_time < b.created_time;  // ì˜¤ëž˜ëœ ìˆœ
        });

        return result;
    }
};
