# ë ˆì´ì•„ì›ƒ ì¡°ì •
tmux select-layout -t \$TMUX_SESSION:0 tiled

echo -e "\${GREEN}âœ… Sessions created!\${NC}"
echo -e "\${YELLOW}Attaching to tmux session...\${NC}"

# ì„¸ì…˜ ì—°ê²°
tmux attach-session -t \$TMUX_SESSION
"@

    # WSLì— ìŠ¤í¬ë¦½íŠ¸ ì €ì¥
    $scriptContent = $tmuxCommands -replace "`r`n", "`n"
    $tempScript = "$env:TEMP\claude-tmux-temp.sh"
    [System.IO.File]::WriteAllText($tempScript, $scriptContent)

    # WSLë¡œ ë³µì‚¬ ë° ì‹¤í–‰
    $wslScript = "/tmp/claude-tmux-$(Get-Random).sh"
    wsl -d Ubuntu cp $(wsl wslpath -a $tempScript) $wslScript
