function Get-Claude-Sessions {
    Write-Host "=== Active Claude tmux Sessions ===" -ForegroundColor Cyan
    wsl -d Ubuntu -- tmux list-sessions 2>/dev/null | ForEach-Object {
        if ($_ -match "claude") {
            Write-Host $_ -ForegroundColor Green
        }
    }
}

# ì„¸ì…˜ ì¢…ë£Œ
function Stop-Claude-Sessions {
    Write-Host "Stopping all Claude tmux sessions..." -ForegroundColor Yellow
    wsl -d Ubuntu -- bash -c "tmux list-sessions 2>/dev/null | grep claude | cut -d: -f1 | xargs -r -I {} tmux kill-session -t {}"
