    Write-Host "2. View active sessions" -ForegroundColor White
    Write-Host "3. Stop all sessions" -ForegroundColor White
    Write-Host "4. Start with monitoring" -ForegroundColor White
    Write-Host "5. Exit" -ForegroundColor White
    Write-Host ""
    Write-Host "â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”" -ForegroundColor Cyan

    $choice = Read-Host "Select option (1-5)"

    switch ($choice) {
        "1" {
            $projectName = Read-Host "Enter project name (default: my-project)"
            if ([string]::IsNullOrWhiteSpace($projectName)) {
                $projectName = "my-project"
            }
            Start-Claude-Tmux -Project $projectName
        }
        "2" {
            Get-Claude-Sessions
            Read-Host "Press Enter to continue"
            Show-Menu
        }
